'use client';

import { ThaiWatsuduInput } from '@/lib/types/manual-comparison';
import { THAI_WATSADU_COLOR } from '@/lib/constants/competitors';
import { Tooltip } from '@/components/ui/Tooltip';
import Image from 'next/image';

interface ThaiWatsuduInputCardProps {
  value: ThaiWatsuduInput;
  onChange: (value: ThaiWatsuduInput) => void;
  disabled?: boolean;
  errors?: {
    sku?: string;
    url?: string;
  };
}

export function ThaiWatsuduInputCard({
  value,
  onChange,
  disabled = false,
  errors,
}: ThaiWatsuduInputCardProps) {
  return (
    <div className="bg-red-50 border-2 border-red-600 rounded-xl overflow-hidden shadow-md hover:shadow-lg transition-all">
      {/* Header with Thai Watsadu branding */}
      <div
        className="px-4 py-3 flex items-center gap-2"
        style={{ backgroundColor: THAI_WATSADU_COLOR }}
      >
        <Image
          src="/logos/thaiwatsadu.svg"
          alt="Thai Watsadu"
          width={24}
          height={24}
          className="object-contain"
        />
        <span className="text-sm font-bold text-white">Thai Watsadu (Source Product)</span>
      </div>

      {/* Input Fields */}
      <div className="p-6 bg-white space-y-4">
        {/* SKU Input */}
        <div className="space-y-2">
          <div className="flex items-center gap-2">
            <label className="block text-sm font-semibold text-gray-900">
              SKU <span className="text-red-600">*</span>
            </label>
            <Tooltip content="Product SKU from Thai Watsadu" placement="right">
              <span className="text-gray-400" />
            </Tooltip>
          </div>
          <div
            className="bg-gray-50 rounded-lg px-4 py-3 min-h-[52px] cursor-text border-2 border-gray-200 hover:border-red-300 focus-within:border-red-500 focus-within:ring-2 focus-within:ring-red-100 transition-all"
            onClick={() => {
              const input = document.getElementById('thai-watsadu-sku');
              input?.focus();
            }}
          >
            <input
              id="thai-watsadu-sku"
              type="text"
              value={value.sku}
              onChange={(e) => onChange({ ...value, sku: e.target.value })}
              placeholder="Enter product SKU"
              disabled={disabled}
              className={`
                w-full bg-transparent border-none outline-none text-gray-900 placeholder-gray-400 font-medium
                disabled:cursor-not-allowed disabled:opacity-50
              `}
            />
          </div>
          {errors?.sku && (
            <p className="text-xs text-red-600 font-medium">{errors.sku}</p>
          )}
        </div>

        {/* URL Input */}
        <div className="space-y-2">
          <div className="flex items-center gap-2">
            <label className="block text-sm font-semibold text-gray-900">
              URL <span className="text-red-600">*</span>
            </label>
            <Tooltip content="Full product page URL from Thai Watsadu website" placement="right">
              <span className="text-gray-400" />
            </Tooltip>
          </div>
          <div
            className="bg-gray-50 rounded-lg px-4 py-3 min-h-[52px] cursor-text border-2 border-gray-200 hover:border-red-300 focus-within:border-red-500 focus-within:ring-2 focus-within:ring-red-100 transition-all"
            onClick={() => {
              const input = document.getElementById('thai-watsadu-url');
              input?.focus();
            }}
          >
            <input
              id="thai-watsadu-url"
              type="url"
              value={value.url}
              onChange={(e) => onChange({ ...value, url: e.target.value })}
              placeholder="https://www.thaiwatsadu.com/..."
              disabled={disabled}
              className={`
                w-full bg-transparent border-none outline-none text-gray-900 placeholder-gray-400 font-medium
                disabled:cursor-not-allowed disabled:opacity-50
              `}
            />
          </div>
          {errors?.url && (
            <p className="text-xs text-red-600 font-medium">{errors.url}</p>
          )}
        </div>
      </div>
    </div>
  );
}

