     1→'use client';
     2→
     3→import { COMPETITORS, COMPETITOR_LIST } from '@/lib/constants/competitors';
     4→import { CompetitorRetailer } from '@/lib/types/manual-comparison';
     5→import { X } from 'lucide-react';
     6→import { Tooltip } from '@/components/ui/Tooltip';
     7→import Image from 'next/image';
     8→
     9→interface CompetitorInputCardProps {
    10→  id: string;
    11→  retailer: CompetitorRetailer | '';
    12→  url: string;
    13→  onRetailerChange: (retailer: CompetitorRetailer) => void;
    14→  onUrlChange: (url: string) => void;
    15→  onRemove: () => void;
    16→  disabled?: boolean;
    17→  error?: string;
    18→  usedRetailers?: CompetitorRetailer[];
    19→}
    20→
    21→export function CompetitorInputCard({
    22→  id,
    23→  retailer,
    24→  url,
    25→  onRetailerChange,
    26→  onUrlChange,
    27→  onRemove,
    28→  disabled = false,
    29→  error,
    30→  usedRetailers = [],
    31→}: CompetitorInputCardProps) {
    32→  const selectedCompetitor = retailer ? COMPETITORS[retailer] : null;
    33→  const borderColor = selectedCompetitor?.color || '#E5E7EB';
    34→
    35→  return (
    36→    <div
    37→      className="overflow-hidden rounded-xl bg-white shadow-md transition-all hover:shadow-lg"
    38→      style={{ borderLeft: `6px solid ${borderColor}` }}
    39→    >
    40→      {/* Header */}
    41→      <div className="flex items-center justify-between border-b-2 border-gray-200 bg-gray-50 px-4 py-3">
    42→        <div className="flex items-center gap-2">
    43→          {selectedCompetitor?.logo && (
    44→            <Image
    45→              src={selectedCompetitor.logo}
    46→              alt={selectedCompetitor.name}
    47→              width={24}
    48→              height={24}
    49→              className="object-contain"
    50→            />
    51→          )}
    52→          <span className="text-sm font-bold text-gray-900">
    53→            {selectedCompetitor?.name || 'Competitor'}
    54→          </span>
    55→        </div>
    56→        <button
    57→          type="button"
    58→          onClick={onRemove}
    59→          disabled={disabled}
    60→          className="rounded p-1 text-gray-400 transition-colors hover:bg-red-50 hover:text-red-500 disabled:cursor-not-allowed"
    61→          aria-label="Remove competitor"
    62→        >
    63→          <X className="h-5 w-5" />
    64→        </button>
    65→      </div>
    66→
    67→      {/* Form Fields */}
    68→      <div className="space-y-4 p-4">
    69→        {/* Retailer Dropdown */}
    70→        <div>
    71→          <div className="mb-2 flex items-center gap-2">
    72→            <label
    73→              htmlFor={`${id}-retailer`}
    74→              className="block text-sm font-semibold text-gray-900"
    75→            >
    76→              Retailer Name
    77→            </label>
    78→            <Tooltip
    79→              content="Select the competitor retailer"
    80→              placement="right"
    81→            >
    82→              <span className="text-gray-400" />
    83→            </Tooltip>
    84→          </div>
    85→          <select
    86→            id={`${id}-retailer`}
    87→            value={retailer}
    88→            onChange={(e) =>
    89→              onRetailerChange(e.target.value as CompetitorRetailer)
    90→            }
    91→            disabled={disabled}
    92→            className="w-full rounded-lg border-2 border-gray-200 bg-gray-50 px-4 py-3 font-medium text-gray-900 outline-none transition-all hover:border-gray-300 focus:border-cyan-500 focus:ring-2 focus:ring-cyan-100 disabled:cursor-not-allowed disabled:opacity-50"
    93→          >
    94→            <option value="">Select a retailer...</option>
    95→            {COMPETITOR_LIST.map((comp) => {
    96→              const isUsed = usedRetailers.includes(comp.id);
    97→              const isCurrentSelection = retailer === comp.id;
    98→
    99→              return (
   100→                <option
   101→                  key={comp.id}
   102→                  value={comp.id}
   103→                  disabled={isUsed && !isCurrentSelection}
   104→                >
   105→                  {comp.name} {isUsed && !isCurrentSelection ? '(Already selected)' : ''}
   106→                </option>
   107→              );
   108→            })}
   109→          </select>
   110→        </div>
   111→
   112→        {/* Product URL Input */}
   113→        <div>
   114→          <div className="mb-2 flex items-center gap-2">
