    61→
    62→export default function ManualComparisonPage() {
    63→  // Stage management
    64→  const [stage, setStage] = useState<ComparisonStage>('input');
    65→
    66→  // Form state
    67→  const [thaiWatsuduInput, setThaiWatsuduInput] = useState<ThaiWatsuduInput>({
    68→    sku: '',
    69→    url: '',
    70→  });
    71→  const [competitorEntries, setCompetitorEntries] = useState<CompetitorEntry[]>([
    72→    { id: `competitor-${Date.now()}`, retailer: '', url: '' },
    73→  ]);
    74→
    75→  // Results state
    76→  const [comparisonData, setComparisonData] = useState<ComparisonResponse | null>(null);
    77→
    78→  // UI state
    79→  const [isSubmitting, setIsSubmitting] = useState(false);
    80→  const [errors, setErrors] = useState<ValidationErrors>({});
    81→  const [toast, setToast] = useState<{
    82→    isVisible: boolean;
    83→    message: string;
