    85→  }>({
    86→    isVisible: false,
    87→    message: '',
    88→    variant: 'success',
    89→  });
    90→
    91→  // Add new competitor entry
    92→  const handleAddCompetitor = useCallback(() => {
    93→    const newEntry: CompetitorEntry = {
    94→      id: `competitor-${Date.now()}-${Math.random()}`,
    95→      retailer: '',
    96→      url: '',
    97→    };
    98→    setCompetitorEntries((prev) => [...prev, newEntry]);
    99→  }, []);
   100→
   101→  // Remove competitor entry
   102→  const handleRemoveCompetitor = useCallback((id: string) => {
   103→    setCompetitorEntries((prev) => prev.filter((entry) => entry.id !== id));
   104→  }, []);
   105→
   106→  // Update competitor retailer
   107→  const handleCompetitorRetailerChange = useCallback(
   108→    (id: string, retailer: CompetitorRetailer) => {
   109→      setCompetitorEntries((prev) =>
   110→        prev.map((entry) =>
   111→          entry.id === id ? { ...entry, retailer } : entry
   112→        )
   113→      );
   114→    },
   115→    []
   116→  );
   117→
   118→  // Update competitor URL
   119→  const handleCompetitorUrlChange = useCallback((id: string, url: string) => {
   120→    setCompetitorEntries((prev) =>
   121→      prev.map((entry) => (entry.id === id ? { ...entry, url } : entry))
   122→    );
   123→  }, []);
   124→
   125→  // Get list of used retailers
   126→  const getUsedRetailers = useCallback((): CompetitorRetailer[] => {
   127→    return competitorEntries
   128→      .map((entry) => entry.retailer)
   129→      .filter((retailer): retailer is CompetitorRetailer => retailer !== '');
   130→  }, [competitorEntries]);
   131→
   132→  // Validate input stage
   133→  const validateInputStage = useCallback((): boolean => {
   134→    const newErrors: ValidationErrors = {};
   135→
   136→    // Validate Thai Watsadu
   137→    if (!thaiWatsuduInput.sku.trim()) {
   138→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, sku: 'SKU is required' };
   139→    }
   140→    if (!thaiWatsuduInput.url.trim()) {
   141→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, url: 'URL is required' };
   142→    }
   143→
   144→    // Validate competitors
   145→    const competitorErrors: Record<string, string> = {};
   146→    let hasAtLeastOneCompetitor = false;
   147→
   148→    competitorEntries.forEach((entry) => {
   149→      if (entry.retailer && entry.url.trim()) {
   150→        hasAtLeastOneCompetitor = true;
   151→      }
   152→
   153→      if (entry.retailer && !entry.url.trim()) {
   154→        competitorErrors[entry.id] = 'URL is required';
   155→      }
   156→      if (!entry.retailer && entry.url.trim()) {
   157→        competitorErrors[entry.id] = 'Retailer selection is required';
   158→      }
   159→    });
   160→
   161→    if (!hasAtLeastOneCompetitor) {
   162→      newErrors.general = 'Please add at least one competitor with retailer and URL';
   163→    }
   164→
   165→    if (Object.keys(competitorErrors).length > 0) {
   166→      newErrors.competitors = competitorErrors;
   167→    }
   168→
   169→    setErrors(newErrors);
   170→    return Object.keys(newErrors).length === 0;
   171→  }, [thaiWatsuduInput, competitorEntries]);
   172→
   173→  // Handle Input -> Review transition
