   368→                className="flex items-center gap-2 rounded-lg border-2 border-gray-300 bg-white px-8 py-3 font-semibold text-gray-700 transition-colors hover:bg-gray-50"
   369→              >
   370→                <RotateCcw className="h-5 w-5" />
   371→                Start New Comparison
   372→              </button>
   373→            </div>
   374→          </div>
   375→        )}
   376→      </div>
   377→
   378→      {/* Loading Overlay */}
   379→      {isSubmitting && <LoadingOverlay message="Processing comparison..." />}
   380→
   381→      {/* Toast Notification */}
   382→      <Toast
   383→        message={toast.message}
   384→        variant={toast.variant}
   385→        isVisible={toast.isVisible}
   386→        onClose={() => setToast({ ...toast, isVisible: false })}
   387→      />
   388→    </MainLayout>
   389→  );
   390→}
   391→

