'use client';

import { COMPETITORS, COMPETITOR_LIST } from '@/lib/constants/competitors';
import { CompetitorRetailer } from '@/lib/types/manual-comparison';
import { X } from 'lucide-react';
import { Tooltip } from '@/components/ui/Tooltip';
import Image from 'next/image';

interface CompetitorInputCardProps {
  id: string;
  retailer: CompetitorRetailer | '';
  url: string;
  onRetailerChange: (retailer: CompetitorRetailer) => void;
  onUrlChange: (url: string) => void;
  onRemove: () => void;
  disabled?: boolean;
  error?: string;
  usedRetailers?: CompetitorRetailer[];
}

export function CompetitorInputCard({
  id,
  retailer,
  url,
  onRetailerChange,
  onUrlChange,
  onRemove,
  disabled = false,
  error,
  usedRetailers = [],
}: CompetitorInputCardProps) {
  const selectedCompetitor = retailer ? COMPETITORS[retailer] : null;
  const borderColor = selectedCompetitor?.color || '#E5E7EB';

  return (
    <div
      className="overflow-hidden rounded-xl bg-white shadow-md transition-all hover:shadow-lg"
      style={{ borderLeft: `6px solid ${borderColor}` }}
    >
      {/* Header */}
      <div className="flex items-center justify-between border-b-2 border-gray-200 bg-gray-50 px-4 py-3">
        <div className="flex items-center gap-2">
          {selectedCompetitor?.logo && (
            <Image
              src={selectedCompetitor.logo}
              alt={selectedCompetitor.name}
              width={24}
              height={24}
              className="object-contain"
            />
          )}
          <span className="text-sm font-bold text-gray-900">
            {selectedCompetitor?.name || 'Competitor'}
          </span>
        </div>
        <button
          type="button"
          onClick={onRemove}
          disabled={disabled}
          className="rounded p-1 text-gray-400 transition-colors hover:bg-red-50 hover:text-red-500 disabled:cursor-not-allowed"
          aria-label="Remove competitor"
        >
          <X className="h-5 w-5" />
        </button>
      </div>

      {/* Form Fields */}
      <div className="space-y-4 p-4">
        {/* Retailer Dropdown */}
        <div>
          <div className="mb-2 flex items-center gap-2">
            <label
              htmlFor={`${id}-retailer`}
              className="block text-sm font-semibold text-gray-900"
            >
              Retailer Name
            </label>
            <Tooltip
              content="Select the competitor retailer"
              placement="right"
            >
              <span className="text-gray-400" />
            </Tooltip>
          </div>
          <select
            id={`${id}-retailer`}
            value={retailer}
            onChange={(e) =>
              onRetailerChange(e.target.value as CompetitorRetailer)
            }
            disabled={disabled}
            className="w-full rounded-lg border-2 border-gray-200 bg-gray-50 px-4 py-3 font-medium text-gray-900 outline-none transition-all hover:border-gray-300 focus:border-cyan-500 focus:ring-2 focus:ring-cyan-100 disabled:cursor-not-allowed disabled:opacity-50"
          >
            <option value="">Select a retailer...</option>
            {COMPETITOR_LIST.map((comp) => {
              const isUsed = usedRetailers.includes(comp.id);
              const isCurrentSelection = retailer === comp.id;

              return (
                <option
                  key={comp.id}
                  value={comp.id}
                  disabled={isUsed && !isCurrentSelection}
                >
                  {comp.name} {isUsed && !isCurrentSelection ? '(Already selected)' : ''}
                </option>
              );
            })}
          </select>
        </div>

        {/* Product URL Input */}
        <div>
          <div className="mb-2 flex items-center gap-2">
            <label
              htmlFor={`${id}-url`}
              className="block text-sm font-semibold text-gray-900"
            >
              Product URL
            </label>
            <Tooltip
              content={
                selectedCompetitor
                  ? `Product URL from ${selectedCompetitor.name} website`
                  : 'Select a retailer first'
              }
              placement="right"
            >
              <span className="text-gray-400" />
            </Tooltip>
          </div>
          <div
            className="min-h-[52px] cursor-text rounded-lg border-2 border-gray-200 bg-gray-50 px-4 py-3 transition-all hover:border-gray-300 focus-within:border-cyan-500 focus-within:ring-2 focus-within:ring-cyan-100"
            onClick={() => {
              document.getElementById(`${id}-url`)?.focus();
            }}
          >
            <input
              id={`${id}-url`}
              type="url"
              value={url}
              onChange={(e) => onUrlChange(e.target.value)}
              placeholder={
                selectedCompetitor
                  ? `Enter ${selectedCompetitor.name} product URL`
                  : 'Select a retailer first'
              }
              disabled={disabled || !retailer}
              className="w-full border-none bg-transparent font-medium text-gray-900 outline-none placeholder-gray-400 disabled:cursor-not-allowed disabled:opacity-50"
            />
          </div>
        </div>

        {error && <p className="text-xs font-medium text-red-600">{error}</p>}
      </div>
    </div>
  );
}

