'use client';

import React from 'react';
import { ArrowLeft, Check, ExternalLink } from 'lucide-react';
import { COMPETITORS, THAI_WATSADU_COLOR } from '@/lib/constants/competitors';
import Image from 'next/image';

interface ThaiWatsuduInput {
  sku: string;
  url: string;
}

interface CompetitorEntry {
  id: string;
  retailer: string;
  url: string;
}

interface ReviewConfirmPanelProps {
  thaiWatsuduInput: ThaiWatsuduInput;
  competitorEntries: CompetitorEntry[];
  onEdit: () => void;
  onConfirm: () => void;
}

export function ReviewConfirmPanel({
  thaiWatsuduInput,
  competitorEntries,
  onEdit,
  onConfirm,
}: ReviewConfirmPanelProps) {
  return (
    <div className="mx-auto max-w-4xl space-y-6">
      {/* Header */}
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900">
          Review Your Comparison
        </h2>
        <p className="mt-2 text-gray-600">
          Please review the details below before proceeding
        </p>
      </div>

      {/* Thai Watsadu Card */}
      <div className="rounded-lg border-2 border-red-200 bg-white p-6 shadow-sm">
        <div className="mb-4 flex items-center gap-2">
          <div className="h-8 w-1 bg-red-600"></div>
          <h3 className="text-lg font-semibold text-gray-900">
            Thai Watsadu (Source Product)
          </h3>
        </div>
        <div className="space-y-2 pl-3">
          <div>
            <span className="text-sm font-medium text-gray-700">SKU: </span>
            <span className="text-sm text-gray-900">
              {thaiWatsuduInput.sku}
            </span>
          </div>
          <div>
            <span className="text-sm font-medium text-gray-700">URL: </span>
            <a
              href={thaiWatsuduInput.url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-sm text-blue-600 hover:underline"
            >
              {thaiWatsuduInput.url}
            </a>
          </div>
        </div>
      </div>

      {/* Competitors Card */}
      <div className="rounded-lg border border-gray-200 bg-white p-6 shadow-sm">
        <h3 className="mb-4 text-lg font-semibold text-gray-900">
          Competitors to Compare ({competitorEntries.length})
        </h3>
        <div className="space-y-4">
          {competitorEntries.map((entry, index) => (
            <div
              key={entry.id}
              className="flex items-start gap-4 rounded-lg border border-gray-200 bg-gray-50 p-4"
            >
              <div className="flex h-8 w-8 flex-shrink-0 items-center justify-center rounded-full bg-blue-100 text-sm font-semibold text-blue-700">
                {index + 1}
              </div>
              <div className="flex-1 space-y-1">
                <div className="font-medium text-gray-900">
                  {entry.retailer}
                </div>
                <a
                  href={entry.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="block text-sm text-blue-600 hover:underline"
                >
                  {entry.url}
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Summary */}
      <div className="rounded-lg border border-blue-200 bg-blue-50 p-4">
        <div className="flex items-center gap-2 text-sm text-blue-900">
          <Check className="h-5 w-5" />
          <span>
            Ready to compare <strong>1 Thai Watsadu product</strong> with{' '}
            <strong>{competitorEntries.length} competitor products</strong>
          </span>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="flex justify-between gap-4">
        <button
          onClick={onEdit}
          className="flex items-center gap-2 rounded-lg border border-gray-300 bg-white px-6 py-3 text-gray-700 transition-colors hover:bg-gray-50"
        >
          <ArrowLeft className="h-5 w-5" />
          Back to Edit
        </button>
        <button
          onClick={onConfirm}
          className="flex items-center gap-2 rounded-lg bg-blue-600 px-8 py-3 font-semibold text-white transition-colors hover:bg-blue-700"
        >
          <Check className="h-5 w-5" />
          Confirm & Compare
        </button>
      </div>
    </div>
  );
}

