   117→      console.log('Pre-filled from product:', productId);
   118→    }
   119→  }, [searchParams]);
   120→
   121→  // Add new competitor entry
   122→  const handleAddCompetitor = useCallback(() => {
   123→    const newEntry: CompetitorEntry = {
   124→      id: `competitor-${Date.now()}-${Math.random()}`,
   125→      retailer: '',
   126→      url: '',
   127→    };
   128→    setCompetitorEntries((prev) => [...prev, newEntry]);
   129→  }, []);
   130→
   131→  // Remove competitor entry
   132→  const handleRemoveCompetitor = useCallback((id: string) => {
