   134→  }, []);
   135→
   136→  // Update competitor retailer
   137→  const handleCompetitorRetailerChange = useCallback(
   138→    (id: string, retailer: CompetitorRetailer) => {
   139→      setCompetitorEntries((prev) =>
   140→        prev.map((entry) =>
   141→          entry.id === id ? { ...entry, retailer } : entry
   142→        )
   143→      );
   144→    },
   145→    []
   146→  );
   147→
   148→  // Update competitor URL
   149→  const handleCompetitorUrlChange = useCallback((id: string, url: string) => {
