   383→                    <span className="text-white text-xl font-bold">!</span>
   384→                  </div>
   385→                  <div>
   386→                    <p className="text-sm font-bold text-red-800">Error</p>
   387→                    <p className="text-sm font-medium text-red-600">{errors.general}</p>
   388→                  </div>
   389→                </div>
   390→              </div>
   391→            )}
   392→
   393→            {/* Next Button */}
   394→            <div className="flex justify-end">
   395→              <button
   396→                type="button"
   397→                onClick={handleGoToReview}
   398→                className="rounded-xl bg-gradient-to-r from-cyan-500 to-blue-500 px-10 py-4 font-bold text-white text-lg shadow-lg transition-all hover:from-cyan-600 hover:to-blue-600 hover:shadow-xl hover:scale-105 active:scale-95"
   399→              >
   400→                Next: Review →
   401→              </button>
   402→            </div>
   403→          </div>
   404→        )}
   405→
   406→        {/* STAGE 2: REVIEW */}
   407→        {stage === 'review' && (
   408→          <ReviewConfirmPanel
   409→            thaiWatsuduInput={thaiWatsuduInput}
   410→            competitorEntries={competitorEntries.filter(
   411→              (entry) => entry.retailer && entry.url.trim()
   412→            ).map(entry => ({
   413→              id: entry.id,
   414→              retailer: entry.retailer as string,
   415→              url: entry.url
   416→            }))}
   417→            onEdit={handleEditInputs}
   418→            onConfirm={handleConfirmAndCompare}
