'use client';

import React from 'react';
import Image from 'next/image';
import { ComparisonStickyHeader } from './ComparisonStickyHeader';
import { ComparisonSpecSection } from './ComparisonSpecSection';
import { PriceDifferenceDisplay } from './PriceDifferenceDisplay';

interface ComparisonProduct {
  sku: string;
  name: string;
  price: number;
  discountPercentage?: number;
  unitPrice?: number;
  imageUrl?: string;
  productUrl: string;
  brand?: string;
  category?: string;
  stockStatus?: string;
  retailer: string;
  retailerLogo?: string;
}

interface AppleStyleComparisonTableProps {
  thaiWatsuduProduct: ComparisonProduct;
  competitorProducts: ComparisonProduct[];
}

export function AppleStyleComparisonTable({
  thaiWatsuduProduct,
  competitorProducts,
}: AppleStyleComparisonTableProps) {
  // Pricing section attributes
  const pricingAttributes = [
    { key: 'price', label: 'Price', type: 'price' as const },
    {
      key: 'discountPercentage',
      label: 'Discount',
      type: 'text' as const,
    },
    { key: 'unitPrice', label: 'Unit Price', type: 'price' as const },
  ];

  // Product info attributes
  const productInfoAttributes = [
    { key: 'name', label: 'Product Name', type: 'text' as const },
    { key: 'sku', label: 'SKU', type: 'text' as const },
    { key: 'brand', label: 'Brand', type: 'text' as const, showIcons: true },
    {
      key: 'category',
      label: 'Category',
      type: 'text' as const,
      showIcons: true,
    },
  ];

  // Availability attributes
  const availabilityAttributes = [
    { key: 'stockStatus', label: 'Stock Status', type: 'status' as const },
    { key: 'productUrl', label: 'Product Link', type: 'url' as const },
  ];

  return (
    <div className="w-full overflow-x-auto rounded-lg border border-gray-200 bg-white shadow-sm">
      <table className="w-full table-fixed border-collapse">
        {/* Sticky Header with Product Images */}
        <ComparisonStickyHeader
          thaiWatsuduProduct={{
            imageUrl: thaiWatsuduProduct.imageUrl,
            name: thaiWatsuduProduct.name,
            retailer: 'Thai Watsadu',
            retailerLogo: thaiWatsuduProduct.retailerLogo,
          }}
          competitorProducts={competitorProducts.map((p) => ({
            imageUrl: p.imageUrl,
            name: p.name,
            retailer: p.retailer,
            retailerLogo: p.retailerLogo,
          }))}
        />

        {/* Compare Row with Product Images */}
        <tbody>
          <tr className="border-b border-gray-200">
            <td className="sticky left-0 bg-white px-6 py-4 text-sm font-medium text-gray-700">
              Compare
            </td>
            <td className="px-4 py-4 text-center">
              <div className="flex flex-col items-center gap-2">
                {thaiWatsuduProduct.imageUrl ? (
                  <div className="relative h-20 w-20">
                    <Image
                      src={thaiWatsuduProduct.imageUrl}
                      alt={thaiWatsuduProduct.name}
                      fill
                      className="object-contain"
                      sizes="80px"
                    />
                  </div>
                ) : (
                  <div className="flex h-20 w-20 items-center justify-center rounded-lg bg-gray-100">
                    <span className="text-xs text-gray-400">No image</span>
                  </div>
                )}
              </div>
            </td>
            {competitorProducts.map((competitor, index) => (
              <td key={index} className="px-4 py-4 text-center">
                <div className="flex flex-col items-center gap-2">
                  {competitor.imageUrl ? (
                    <div className="relative h-20 w-20">
                      <Image
                        src={competitor.imageUrl}
                        alt={competitor.name}
                        fill
                        className="object-contain"
                        sizes="80px"
                      />
                    </div>
                  ) : (
                    <div className="flex h-20 w-20 items-center justify-center rounded-lg bg-gray-100">
                      <span className="text-xs text-gray-400">No image</span>
                    </div>
                  )}
                </div>
              </td>
            ))}
          </tr>

          {/* PRICE COMPARISON Section Header */}
          <tr className="bg-gray-50">
            <td
              colSpan={2 + competitorProducts.length}
              className="px-6 py-3 text-left text-xs font-semibold uppercase tracking-wider text-gray-500"
            >
              PRICE COMPARISON
            </td>
          </tr>
          <tr className="border-b border-gray-200">
            <td className="sticky left-0 bg-white px-6 py-4 text-sm font-medium text-gray-700">
              Price
            </td>
            <td className="px-6 py-4 text-center">
              <div className="text-4xl font-bold text-gray-900">
                ฿{thaiWatsuduProduct.price.toLocaleString('th-TH')}
              </div>
            </td>
            {competitorProducts.map((competitor, index) => (
              <td key={index} className="px-6 py-4 text-center">
                <PriceDifferenceDisplay
                  thaiWatsuduPrice={thaiWatsuduProduct.price}
                  competitorPrice={competitor.price}
                  competitorName={competitor.retailer}
                  showLargePrice={true}
                />
              </td>
            ))}
          </tr>
          <tr>
            <td
              colSpan={2 + competitorProducts.length}
              className="h-4 bg-white"
            ></td>
          </tr>
        </tbody>

        {/* Pricing Section (other pricing details) */}
        <ComparisonSpecSection
          title="Pricing Details"
          attributes={pricingAttributes.filter((attr) => attr.key !== 'price')}
          thaiWatsuduData={thaiWatsuduProduct}
          competitorData={competitorProducts}
        />

        {/* Product Information Section */}
        <ComparisonSpecSection
          title="Product Information"
          attributes={productInfoAttributes}
          thaiWatsuduData={thaiWatsuduProduct}
          competitorData={competitorProducts}
        />

        {/* Availability Section */}
        <ComparisonSpecSection
          title="Availability"
          attributes={availabilityAttributes}
          thaiWatsuduData={thaiWatsuduProduct}
          competitorData={competitorProducts}
        />
      </table>
    </div>
  );
}

'use client';

import { CompetitorMatchResult, CompetitorRetailer } from '@/lib/types/manual-comparison';
import { COMPETITORS } from '@/lib/constants/competitors';
import { getConfidenceColor, getMatchStatusColor, formatThaiPrice } from '@/lib/utils/comparison-utils';
import { Check, X, ExternalLink, AlertCircle, Loader2 } from 'lucide-react';

interface ComparisonResultCardProps {
  result: CompetitorMatchResult;
  onConfirm: (competitor: CompetitorRetailer) => void;
  onReject: (competitor: CompetitorRetailer) => void;
  disabled?: boolean;
}

export function ComparisonResultCard({
  result,
  onConfirm,
  onReject,
  disabled = false,
}: ComparisonResultCardProps) {
  const competitor = COMPETITORS[result.competitor];
  const statusColors = getMatchStatusColor(result.status);
  const confidenceColor = getConfidenceColor(result.confidence);

  const isVerified = result.userVerified !== undefined;
  const isMatch = result.status === 'match';
  const isPending = result.status === 'pending';
  const isError = result.status === 'error';

  return (
    <div className={`border rounded-lg overflow-hidden ${statusColors.border}`}>
      {/* Header */}
      <div className={`px-4 py-3 ${statusColors.bg} border-b ${statusColors.border}`}>
        <div className="flex items-center justify-between">
          <div>
            <h4 className="font-medium text-gray-900">{competitor.name}</h4>
            <p className="text-xs text-gray-500">{competitor.domain}</p>
          </div>
          {!isPending && !isError && (
            <span className={`px-2 py-1 text-xs font-medium rounded-full ${confidenceColor}`}>
              {result.confidence}% confidence
            </span>
          )}
        </div>
      </div>

      {/* Content */}
      <div className="p-4 bg-white">
        {isPending && (
          <div className="flex items-center justify-center py-8">
            <Loader2 className="w-6 h-6 text-sky-500 animate-spin" />
            <span className="ml-2 text-sm text-gray-500">Processing...</span>
          </div>
        )}

        {isError && (
          <div className="flex items-center gap-2 py-4 text-red-600">
            <AlertCircle className="w-5 h-5" />
            <span className="text-sm">{result.errorMessage || 'An error occurred'}</span>
          </div>
        )}

        {!isPending && !isError && result.matchedProduct && (
          <div className="space-y-3">
            {/* Matched Product URL */}
            <a
              href={result.matchedProduct.productUrl}
              target="_blank"
              rel="noopener noreferrer"
              className="block text-sm text-sky-600 hover:text-sky-700 hover:underline break-all"
            >
              {result.matchedProduct.productUrl}
              <ExternalLink className="w-3 h-3 inline ml-1" />
            </a>

            {/* Matched Product Details */}
            <div className="bg-gray-50 rounded-lg p-3 space-y-2">
              <p className="text-sm font-medium text-gray-900">{result.matchedProduct.name}</p>
              <div className="flex items-center gap-4 text-sm">
                <span className="text-gray-500">SKU: <span className="font-mono text-gray-900">{result.matchedProduct.sku}</span></span>
                <span className="text-gray-500">Price: <span className="font-medium text-sky-600">{formatThaiPrice(result.matchedProduct.price)}</span></span>
              </div>
            </div>
          </div>
        )}

        {!isPending && !isError && !result.matchedProduct && result.status === 'not_match' && (
          <div className="py-4 text-center text-gray-500">
            <p className="text-sm">No matching product found</p>
          </div>
        )}

        {/* Action Buttons */}
        {!isPending && !isError && (
          <div className="flex items-center gap-2 mt-4 pt-4 border-t border-gray-100">
            {isVerified ? (
              <div className={`flex items-center gap-2 px-4 py-2 rounded-lg ${
                result.userVerified ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
              }`}>
                {result.userVerified ? (
                  <>
                    <Check className="w-4 h-4" />
                    <span className="text-sm font-medium">Confirmed as Match</span>
                  </>
                ) : (
                  <>
                    <X className="w-4 h-4" />
                    <span className="text-sm font-medium">Marked as Incorrect</span>
                  </>
                )}
              </div>
            ) : (
              <>
                <button
                  type="button"
                  onClick={() => onConfirm(result.competitor)}
                  disabled={disabled}
                  className="flex items-center gap-2 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  <Check className="w-4 h-4" />
                  <span className="text-sm font-medium">Match</span>
                </button>
                <button
                  type="button"
                  onClick={() => onReject(result.competitor)}
                  disabled={disabled}
                  className="flex items-center gap-2 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  <X className="w-4 h-4" />
                  <span className="text-sm font-medium">Not Match</span>
                </button>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

