'use client';

import React from 'react';
import {
  calculatePriceDifference,
  getComparisonColor,
  formatThaiCurrency,
  formatPercentage,
} from '@/lib/utils/comparison-display-utils';

interface PriceDifferenceDisplayProps {
  thaiWatsuduPrice: number;
  competitorPrice: number;
  competitorName: string;
  showLargePrice?: boolean;
}

export function PriceDifferenceDisplay({
  thaiWatsuduPrice,
  competitorPrice,
  competitorName,
  showLargePrice = true,
}: PriceDifferenceDisplayProps) {
  const { amountDifference, percentageDifference, status } =
    calculatePriceDifference(thaiWatsuduPrice, competitorPrice);
  const colors = getComparisonColor(status);

  if (status === 'same') {
    return (
      <div className="flex flex-col items-center justify-center py-2">
        {showLargePrice && (
          <div className="text-4xl font-bold text-gray-900">
            {formatThaiCurrency(competitorPrice)}
          </div>
        )}
        <div className="mt-1 text-sm text-gray-600">Same price</div>
      </div>
    );
  }

  const differenceText =
    status === 'cheaper'
      ? `${formatThaiCurrency(amountDifference)} cheaper`
      : `${formatThaiCurrency(amountDifference)} more expensive`;

  return (
    <div className="flex flex-col items-center justify-center py-2">
      {showLargePrice && (
        <div className="text-4xl font-bold text-gray-900">
          {formatThaiCurrency(competitorPrice)}
        </div>
      )}
      <div
        className={`mt-2 rounded-full px-3 py-1 text-sm font-medium ${colors.bg} ${colors.text} ${colors.border} border`}
      >
        <div className="text-center">
          <div className="font-semibold">{differenceText}</div>
          <div className="text-xs opacity-90">
            ({formatPercentage(percentageDifference)})
          </div>
        </div>
      </div>
    </div>
  );
}

