     5→
     6→// Competitor retailer type (excludes Thai Watsadu as source)
     7→export type CompetitorRetailer =
     8→  | 'HOMEPRO'
     9→  | 'MEGA_HOME'
    10→  | 'BOONTHAVORN'
    11→  | 'GLOBAL_HOUSE'
    12→  | 'DOHOME';
    13→
    14→// Competitor info with display properties
    15→export interface CompetitorInfo {
    16→  id: CompetitorRetailer;
    17→  name: string;
    18→  nameTh: string;
    19→  domain: string;
    20→  logo?: string;
    21→  color: string; // Brand color for UI display
    22→}
    23→
    24→// Input form state (legacy)
    25→export interface ManualComparisonInput {
    26→  thaiwatsuduInput: string; // SKU or URL
    27→  inputType: 'sku' | 'url';
    28→  selectedCompetitors: CompetitorRetailer[];
    29→  competitorUrls: Partial<Record<CompetitorRetailer, string>>;
    30→}
    31→
    32→// New Manual Comparison Journey - Side A (Thai Watsadu input)
    33→export interface ThaiWatsuduInput {
    34→  sku: string; // Required
    35→  url: string; // Required
    36→}
    37→
    38→// New Manual Comparison Journey - Competitor URL entry
    39→export interface CompetitorUrlEntry {
    40→  id: string; // Unique ID for the entry
    41→  retailer: CompetitorRetailer;
    42→  urls: string[]; // Can have multiple URLs per competitor
    43→}
    44→
    45→// New Manual Comparison Journey - Full input state
    46→export interface ManualComparisonInputV2 {
    47→  thaiWatsadu: ThaiWatsuduInput;
    48→  competitors: CompetitorUrlEntry[];
    49→}
    50→
    51→// Source product fetched from Thaiwatsadu
    52→export interface SourceProduct {
    53→  sku: string;
    54→  name: string;
    55→  nameTh?: string;
    56→  price: number;
    57→  imageUrl: string;
    58→  productUrl: string;
    59→  category?: string;
    60→  brand?: string;
    61→}
    62→
    63→// Match status for competitor results
    64→export type MatchStatus = 'match' | 'not_match' | 'pending' | 'error';
    65→
    66→// Individual URL comparison result
    67→export interface UrlComparisonResult {
    68→  url: string;
    69→  status: MatchStatus;
    70→  confidence: number; // 0-100
    71→  matchedProduct?: {
    72→    sku: string;
