    77→  };
    78→  errorMessage?: string;
    79→}
    80→
    81→// Match result for each competitor (updated with multiple URL support)
    82→export interface CompetitorMatchResult {
    83→  competitor: CompetitorRetailer;
    84→  status: MatchStatus;
    85→  confidence: number; // 0-100
    86→  matchedProduct?: {
    87→    sku: string;
    88→    name: string;
    89→    price: number;
    90→    imageUrl: string;
    91→    productUrl: string;
    92→  };
    93→  userVerified?: boolean;
    94→  errorMessage?: string;
    95→  // New fields for multi-URL comparison
    96→  urlResults?: UrlComparisonResult[];
    97→  matchCount?: number;
    98→  notMatchCount?: number;
    99→}
   100→
   101→// API request payload (legacy)
   102→export interface ManualComparisonRequest {
   103→  sourceProduct: SourceProduct;
   104→  competitors: CompetitorRetailer[];
   105→  competitorUrls?: Partial<Record<CompetitorRetailer, string>>;
