     1→'use client';
     2→
     3→import { CompetitorMatchResult, CompetitorRetailer } from '@/lib/types/manual-comparison';
     4→import { COMPETITORS } from '@/lib/constants/competitors';
     5→import { RefreshCw, Check, Loader2 } from 'lucide-react';
     6→
     7→interface UserValidationPanelProps {
     8→  results: CompetitorMatchResult[];
     9→  onRetry: () => void;
    10→  onConfirm: () => void;
    11→  isProcessing?: boolean;
    12→}
    13→
    14→export function UserValidationPanel({
    15→  results,
    16→  onRetry,
    17→  onConfirm,
    18→  isProcessing = false,
    19→}: UserValidationPanelProps) {
    20→  const hasPending = results.some((r) => r.status === 'pending');
    21→
    22→  return (
    23→    <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
    24→      {/* Header */}
    25→      <div className="px-4 py-3 border-b border-gray-200">
    26→        <h3 className="text-sm font-semibold text-gray-900">User validate</h3>
    27→      </div>
    28→
    29→      {/* Results List */}
    30→      <div className="divide-y divide-gray-100">
    31→        {results.map((result) => (
    32→          <CompetitorValidationRow key={result.competitor} result={result} />
    33→        ))}
    34→      </div>
    35→
    36→      {/* Action Buttons */}
    37→      <div className="p-4 border-t border-gray-200 flex items-center justify-center gap-4">
    38→        <button
    39→          type="button"
    40→          onClick={onRetry}
    41→          disabled={isProcessing || hasPending}
    42→          className="flex items-center gap-2 px-6 py-2.5 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
    43→        >
    44→          {isProcessing ? (
    45→            <Loader2 className="w-4 h-4 animate-spin" />
    46→          ) : (
    47→            <RefreshCw className="w-4 h-4" />
    48→          )}
    49→          <span className="font-medium">Retry</span>
    50→        </button>
    51→        <button
    52→          type="button"
    53→          onClick={onConfirm}
    54→          disabled={isProcessing || hasPending}
    55→          className="flex items-center gap-2 px-6 py-2.5 bg-gray-900 text-white rounded-lg hover:bg-gray-800 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
    56→        >
    57→          {isProcessing ? (
    58→            <Loader2 className="w-4 h-4 animate-spin" />
    59→          ) : (
    60→            <Check className="w-4 h-4" />
    61→          )}
    62→          <span className="font-medium">Confirm</span>
    63→        </button>
    64→      </div>
    65→    </div>
    66→  );
    67→}
    68→
    69→interface CompetitorValidationRowProps {
    70→  result: CompetitorMatchResult;
    71→}
    72→
    73→function CompetitorValidationRow({ result }: CompetitorValidationRowProps) {
    74→  const competitor = COMPETITORS[result.competitor];
    75→  const matchCount = result.matchCount ?? (result.status === 'match' ? 1 : 0);
    76→  const notMatchCount = result.notMatchCount ?? (result.status === 'not_match' ? 1 : 0);
    77→  const totalUrls = matchCount + notMatchCount;
    78→  const isPending = result.status === 'pending';
    79→
    80→  return (
    81→    <div className="p-4">
    82→      {/* Competitor Header */}
