    21→import {
    22→  saveManualComparisonProduct,
    23→  ComparisonTableData,
    24→} from '@/lib/utils/manual-comparison-storage';
    25→
    26→// Stage type for the 3-stage flow
    27→type ComparisonStage = 'input' | 'review' | 'results';
    28→
    29→// Competitor entry structure
    30→interface CompetitorEntry {
    31→  id: string;
    32→  retailer: CompetitorRetailer | '';
    33→  url: string;
    34→}
    35→
    36→// Comparison product structure
    37→interface ComparisonProduct {
    38→  sku: string;
    39→  name: string;
    40→  price: number;
    41→  discountPercentage?: number;
    42→  unitPrice?: number;
    43→  imageUrl?: string;
    44→  productUrl: string;
    45→  brand?: string;
    46→  category?: string;
    47→  stockStatus?: string;
    48→  retailer: string;
    49→  retailerLogo?: string;
    50→}
    51→
    52→// Comparison response structure (same as ComparisonTableData)
    53→interface ComparisonResponse {
    54→  thaiWatsadu: ComparisonProduct;
    55→  competitors: ComparisonProduct[];
    56→}
    57→
    58→// Validation error types
    59→interface ValidationErrors {
    60→  thaiWatsadu?: {
    61→    sku?: string;
    62→    url?: string;
    63→  };
    64→  competitors?: Record<string, string>;
    65→  general?: string;
    66→}
    67→
    68→function ManualComparisonContent() {
    69→  const searchParams = useSearchParams();
    70→
    71→  // Stage management
    72→  const [stage, setStage] = useState<ComparisonStage>('input');
    73→
    74→  // Form state
    75→  const [thaiWatsuduInput, setThaiWatsuduInput] = useState<ThaiWatsuduInput>({
    76→    sku: '',
    77→    url: '',
    78→  });
    79→  const [competitorEntries, setCompetitorEntries] = useState<CompetitorEntry[]>([
    80→    { id: `competitor-${Date.now()}`, retailer: '', url: '' },
    81→  ]);
    82→
    83→  // Results state
    84→  const [comparisonData, setComparisonData] = useState<ComparisonResponse | null>(null);
    85→  const [savedProductId, setSavedProductId] = useState<string | null>(null);
    86→
    87→  // UI state
    88→  const [isSubmitting, setIsSubmitting] = useState(false);
    89→  const [errors, setErrors] = useState<ValidationErrors>({});
    90→  const [toast, setToast] = useState<{
    91→    isVisible: boolean;
    92→    message: string;
    93→    variant: ToastVariant;
    94→  }>({
    95→    isVisible: false,
    96→    message: '',
    97→    variant: 'success',
    98→  });
    99→
   100→  // Handle query parameters for pre-filling
   101→  useEffect(() => {
   102→    const sku = searchParams.get('sku');
   103→    const url = searchParams.get('url');
   104→    const productId = searchParams.get('productId');
   105→
   106→    if (sku || url) {
   107→      setThaiWatsuduInput((prev) => ({
   108→        ...prev,
   109→        ...(sku && { sku: sku }),
