import React from 'react';
import { ExternalLink } from 'lucide-react';
import { formatCurrency } from '@/lib/utils/price-utils';

interface PriceCellProps {
  price: number | null;
  originalPrice?: number;
  productUrl: string | null;
  isLowest?: boolean;
  isHighest?: boolean;
}

export const PriceCell: React.FC<PriceCellProps> = ({
  price,
  originalPrice,
  productUrl,
  isLowest,
  isHighest,
}) => {
  const priceText = formatCurrency(price);

  if (price === null) {
    return <span className="text-gray-400">-</span>;
  }

  const colorClass = isLowest
    ? 'text-green-600 font-semibold'
    : isHighest
    ? 'text-red-600 font-semibold'
    : 'text-gray-900';

  if (productUrl) {
    return (
      <a
        href={productUrl}
        target="_blank"
        rel="noopener noreferrer"
        className={`inline-flex items-center gap-1 hover:underline ${colorClass}`}
      >
        {priceText}
        <ExternalLink className="w-3 h-3" />
      </a>
    );
  }

  return <span className={colorClass}>{priceText}</span>;
};

import React from 'react';
import { ProductComparison } from '@/lib/types/price-comparison';
import { PriceComparisonRow } from './PriceComparisonRow';

interface PriceComparisonTableProps {
  products: ProductComparison[];
  isLoading?: boolean;
  onRowClick?: (product: ProductComparison) => void;
  sortBy?: string;
  sortOrder?: 'asc' | 'desc';
  onSort?: (column: string) => void;
}

export const PriceComparisonTable: React.FC<PriceComparisonTableProps> = ({
  products,
  isLoading = false,
  onRowClick,
  sortBy,
  sortOrder,
  onSort,
}) => {
  if (isLoading) {
    return (
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="animate-pulse p-8">
          <div className="h-8 bg-gray-200 rounded mb-4"></div>
          <div className="space-y-3">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="h-12 bg-gray-100 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (products.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow p-8 text-center">
        <p className="text-gray-500 text-lg">No products found</p>
        <p className="text-gray-400 text-sm mt-2">Try adjusting your filters</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow overflow-x-auto">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              No.
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Category
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              SKU
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Product Name
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Brand
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Thai Watsadu
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              HomePro
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Global House
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Do Home
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Boonthavorn
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Status
            </th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {products.map((product, index) => (
            <PriceComparisonRow
              key={product.id}
              product={product}
              rowNumber={index + 1}
              onRowClick={onRowClick}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
};

