'use client';

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { MainLayout } from '@/components/layout/MainLayout';
import { ProductSearchFilter } from '@/components/products/ProductSearchFilter';
import { PriceComparisonTable } from '@/components/products/PriceComparisonTable';
import {
  ProductComparison,
  ProductComparisonListResponse,
  ProductFilterState,
  ValidationStatus,
} from '@/lib/types/price-comparison';
import { exportToCSV } from '@/lib/utils/export-utils';
import { getAllValidationStatusesForAllProducts } from '@/lib/utils/validation-storage';

export default function ProductsPage() {
  const router = useRouter();
  const [data, setData] = useState<ProductComparisonListResponse | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [filters, setFilters] = useState<ProductFilterState>({
    search: '',
    category: null,
    brand: null,
    status: null,
    sortBy: 'name',
    sortOrder: 'asc',
  });
  const [page, setPage] = useState(1);
  const pageSize = 10;
  const [validationStatuses, setValidationStatuses] = useState<Record<string, Record<string, ValidationStatus>>>({});

  // Load validation statuses from localStorage on mount
  useEffect(() => {
    const statuses = getAllValidationStatusesForAllProducts();
    setValidationStatuses(statuses);
  }, []);

  // Fetch products data
  useEffect(() => {
    const fetchProducts = async () => {
      setIsLoading(true);
      try {
        const params = new URLSearchParams({
          page: page.toString(),
          pageSize: pageSize.toString(),
          ...(filters.search && { search: filters.search }),
          ...(filters.category && { category: filters.category }),
          ...(filters.brand && { brand: filters.brand }),
          ...(filters.status && { status: filters.status }),
          sortBy: filters.sortBy,
          sortOrder: filters.sortOrder,
        });

        const response = await fetch(`/api/products/comparison?${params}`);
        if (!response.ok) throw new Error('Failed to fetch products');

        const result: ProductComparisonListResponse = await response.json();
        setData(result);
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProducts();
  }, [filters, page]);

  const handleFilterChange = (newFilters: Partial<ProductFilterState>) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
    setPage(1); // Reset to first page when filters change
  };

  const handleReset = () => {
    setFilters({
      search: '',
      category: null,
      brand: null,
      status: null,
      sortBy: 'name',
      sortOrder: 'asc',
    });
    setPage(1);
  };

  const handleExport = () => {
    if (data?.products) {
      exportToCSV(data.products);
    }
  };

  const handleRowClick = (product: ProductComparison) => {
    router.push(`/products/${product.id}`);
  };

  return (
    <MainLayout>
      <div className="space-y-6">
        {/* Page Header */}
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Products</h1>
          <p className="text-gray-600 mt-1">Compare prices across retailers</p>
        </div>

        {/* Search & Filter */}
        <ProductSearchFilter
          filters={filters}
          categories={data?.filters.categories || []}
          brands={data?.filters.brands || []}
          onFilterChange={handleFilterChange}
          onReset={handleReset}
          onExport={handleExport}
        />

        {/* Price Comparison Section */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">
            Price Comparison
          </h2>

          {/* Table */}
          <PriceComparisonTable
            products={data?.products || []}
            isLoading={isLoading}
            onRowClick={handleRowClick}
            sortBy={filters.sortBy}
            sortOrder={filters.sortOrder}
            validationStatuses={validationStatuses}
          />

          {/* Pagination Info */}
          {data && !isLoading && (
            <div className="flex justify-between items-center">
              <p className="text-sm text-gray-600">
                Showing {data.products.length === 0 ? 0 : (page - 1) * pageSize + 1} to{' '}
                {Math.min(page * pageSize, data.pagination.total)} of{' '}
                {data.pagination.total} products
              </p>

              {/* Pagination Controls */}
              {data.pagination.totalPages > 1 && (
                <div className="flex gap-2">
                  <button
                    onClick={() => setPage((p) => Math.max(1, p - 1))}
                    disabled={page === 1}
                    className="px-3 py-1 border rounded text-sm disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                  >
                    Previous
                  </button>
                  <span className="px-3 py-1 text-sm">
                    Page {page} of {data.pagination.totalPages}
                  </span>
                  <button
                    onClick={() =>
                      setPage((p) => Math.min(data.pagination.totalPages, p + 1))
                    }
                    disabled={page === data.pagination.totalPages}
                    className="px-3 py-1 border rounded text-sm disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
                  >
                    Next
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </MainLayout>
  );
}

import React from 'react';
import { ProductComparison, ValidationStatus } from '@/lib/types/price-comparison';
import { PriceComparisonRow } from './PriceComparisonRow';

interface PriceComparisonTableProps {
  products: ProductComparison[];
  isLoading?: boolean;
  onRowClick?: (product: ProductComparison) => void;
  sortBy?: string;
  sortOrder?: 'asc' | 'desc';
  onSort?: (column: string) => void;
  validationStatuses?: Record<string, Record<string, ValidationStatus>>;
}

export const PriceComparisonTable: React.FC<PriceComparisonTableProps> = ({
  products,
  isLoading = false,
  onRowClick,
  sortBy,
  sortOrder,
  onSort,
  validationStatuses,
}) => {
  if (isLoading) {
    return (
      <div className="overflow-hidden">
        <div className="animate-pulse p-8">
          <div className="h-8 bg-gray-200 rounded mb-4"></div>
          <div className="space-y-3">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="h-12 bg-gray-100 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (products.length === 0) {
    return (
      <div className="p-8 text-center">
        <p className="text-gray-500 text-lg">No products found</p>
        <p className="text-gray-400 text-sm mt-2">Try adjusting your filters</p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto -mx-6">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              NO.
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              SKU
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              PRODUCT NAME
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              BRAND
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              CATEGORY
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              STATUS
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              THAI WATSADU
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              HOMEPRO
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              GLOBAL HOUSE
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              DO HOME
            </th>
            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              BOONTHAVORN
            </th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {products.map((product, index) => (
            <PriceComparisonRow
              key={product.id}
              product={product}
              rowNumber={index + 1}
              onRowClick={onRowClick}
              validationStatuses={validationStatuses}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
};

