     1→'use client';
     2→
     3→import { useState, useCallback, useEffect, Suspense } from 'react';
     4→import { useSearchParams } from 'next/navigation';
     5→import {
     6→  ThaiWatsuduInput,
     7→  CompetitorRetailer,
     8→} from '@/lib/types/manual-comparison';
     9→import {
    10→  ThaiWatsuduInputCard,
    11→  CompetitorInputCard,
    12→} from '@/components/comparison';
    13→import { ReviewConfirmPanel } from '@/components/comparison/ReviewConfirmPanel';
    14→import { AppleStyleComparisonTable } from '@/components/comparison/AppleStyleComparisonTable';
    15→import { StageIndicator } from '@/components/comparison/StageIndicator';
    16→import { CollapsibleStepCard } from '@/components/comparison/CollapsibleStepCard';
    17→import { Toast, ToastVariant } from '@/components/ui/Toast';
    18→import { LoadingOverlay } from '@/components/comparison/LoadingOverlay';
    19→import { MainLayout } from '@/components/layout/MainLayout';
    20→import { Plus, RotateCcw } from 'lucide-react';
    21→
    22→// Stage type for the 3-stage flow
    23→type ComparisonStage = 'input' | 'review' | 'results';
    24→
    25→// Competitor entry structure
    26→interface CompetitorEntry {
    27→  id: string;
    28→  retailer: CompetitorRetailer | '';
    29→  url: string;
    30→}
    31→
    32→// Comparison product structure
    33→interface ComparisonProduct {
    34→  sku: string;
    35→  name: string;
    36→  price: number;
    37→  discountPercentage?: number;
    38→  unitPrice?: number;
    39→  imageUrl?: string;
    40→  productUrl: string;
    41→  brand?: string;
    42→  category?: string;
    43→  stockStatus?: string;
    44→  retailer: string;
    45→  retailerLogo?: string;
    46→}
    47→
    48→// Comparison response structure
    49→interface ComparisonResponse {
    50→  thaiWatsadu: ComparisonProduct;
    51→  competitors: ComparisonProduct[];
    52→}
    53→
    54→// Validation error types
    55→interface ValidationErrors {
    56→  thaiWatsadu?: {
    57→    sku?: string;
    58→    url?: string;
    59→  };
    60→  competitors?: Record<string, string>;
    61→  general?: string;
    62→}
    63→
    64→function ManualComparisonContent() {
    65→  const searchParams = useSearchParams();
    66→
    67→  // Stage management
    68→  const [stage, setStage] = useState<ComparisonStage>('input');
    69→
    70→  // Form state
    71→  const [thaiWatsuduInput, setThaiWatsuduInput] = useState<ThaiWatsuduInput>({
    72→    sku: '',
    73→    url: '',
    74→  });
    75→  const [competitorEntries, setCompetitorEntries] = useState<CompetitorEntry[]>([
    76→    { id: `competitor-${Date.now()}`, retailer: '', url: '' },
    77→  ]);
    78→
    79→  // Results state
    80→  const [comparisonData, setComparisonData] = useState<ComparisonResponse | null>(null);
    81→
    82→  // UI state
    83→  const [isSubmitting, setIsSubmitting] = useState(false);
    84→  const [errors, setErrors] = useState<ValidationErrors>({});
    85→  const [toast, setToast] = useState<{
    86→    isVisible: boolean;
    87→    message: string;
    88→    variant: ToastVariant;
    89→  }>({
    90→    isVisible: false,
    91→    message: '',
    92→    variant: 'success',
    93→  });
    94→
    95→  // Handle query parameters for pre-filling
    96→  useEffect(() => {
    97→    const sku = searchParams.get('sku');
    98→    const url = searchParams.get('url');
    99→    const productId = searchParams.get('productId');
   100→
   101→    if (sku || url) {
   102→      setThaiWatsuduInput((prev) => ({
   103→        ...prev,
   104→        ...(sku && { sku: sku }),
   105→        ...(url && { url: url }),
   106→      }));
   107→    }
   108→
   109→    // Store productId for potential future use (e.g., linking back or storing results)
   110→    if (productId) {
   111→      // You can use this productId later to save manual comparison results
   112→      console.log('Pre-filled from product:', productId);
   113→    }
   114→  }, [searchParams]);
   115→
   116→  // Add new competitor entry
   117→  const handleAddCompetitor = useCallback(() => {
   118→    const newEntry: CompetitorEntry = {
   119→      id: `competitor-${Date.now()}-${Math.random()}`,
   120→      retailer: '',
   121→      url: '',
   122→    };
   123→    setCompetitorEntries((prev) => [...prev, newEntry]);
   124→  }, []);
   125→
   126→  // Remove competitor entry
   127→  const handleRemoveCompetitor = useCallback((id: string) => {
   128→    setCompetitorEntries((prev) => prev.filter((entry) => entry.id !== id));
   129→  }, []);
   130→
   131→  // Update competitor retailer
   132→  const handleCompetitorRetailerChange = useCallback(
   133→    (id: string, retailer: CompetitorRetailer) => {
   134→      setCompetitorEntries((prev) =>
   135→        prev.map((entry) =>
   136→          entry.id === id ? { ...entry, retailer } : entry
   137→        )
   138→      );
   139→    },
   140→    []
   141→  );
   142→
   143→  // Update competitor URL
   144→  const handleCompetitorUrlChange = useCallback((id: string, url: string) => {
   145→    setCompetitorEntries((prev) =>
   146→      prev.map((entry) => (entry.id === id ? { ...entry, url } : entry))
   147→    );
   148→  }, []);
   149→
   150→  // Get list of used retailers
   151→  const getUsedRetailers = useCallback((): CompetitorRetailer[] => {
   152→    return competitorEntries
   153→      .map((entry) => entry.retailer)
   154→      .filter((retailer): retailer is CompetitorRetailer => retailer !== '');
   155→  }, [competitorEntries]);
   156→
   157→  // Validate input stage
   158→  const validateInputStage = useCallback((): boolean => {
   159→    const newErrors: ValidationErrors = {};
   160→
   161→    // Validate Thai Watsadu
   162→    if (!thaiWatsuduInput.sku.trim()) {
   163→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, sku: 'SKU is required' };
   164→    }
   165→    if (!thaiWatsuduInput.url.trim()) {
   166→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, url: 'URL is required' };
   167→    }
   168→
   169→    // Validate competitors
   170→    const competitorErrors: Record<string, string> = {};
   171→    let hasAtLeastOneCompetitor = false;
   172→
   173→    competitorEntries.forEach((entry) => {
   174→      if (entry.retailer && entry.url.trim()) {
   175→        hasAtLeastOneCompetitor = true;
   176→      }
   177→
   178→      if (entry.retailer && !entry.url.trim()) {
   179→        competitorErrors[entry.id] = 'URL is required';
   180→      }
   181→      if (!entry.retailer && entry.url.trim()) {
   182→        competitorErrors[entry.id] = 'Retailer selection is required';
   183→      }
   184→    });
   185→
   186→    if (!hasAtLeastOneCompetitor) {
   187→      newErrors.general = 'Please add at least one competitor with retailer and URL';
   188→    }
   189→
   190→    if (Object.keys(competitorErrors).length > 0) {
   191→      newErrors.competitors = competitorErrors;
   192→    }
   193→
   194→    setErrors(newErrors);
   195→    return Object.keys(newErrors).length === 0;
   196→  }, [thaiWatsuduInput, competitorEntries]);
   197→
   198→  // Handle Input -> Review transition
   199→  const handleGoToReview = useCallback(() => {
   200→    if (validateInputStage()) {
   201→      setStage('review');
   202→    }
   203→  }, [validateInputStage]);
   204→
   205→  // Handle Review -> Input (Edit)
   206→  const handleEditInputs = useCallback(() => {
   207→    setStage('input');
   208→  }, []);
   209→
   210→  // Handle Review -> Results (Submit)
   211→  const handleConfirmAndCompare = useCallback(async () => {
   212→    setIsSubmitting(true);
   213→    setErrors({});
   214→
   215→    try {
   216→      // Filter valid competitor entries
   217→      const validCompetitors = competitorEntries.filter(
   218→        (entry) => entry.retailer && entry.url.trim()
   219→      );
   220→
   221→      const response = await fetch('/api/comparison/manual', {
   222→        method: 'POST',
   223→        headers: {
   224→          'Content-Type': 'application/json',
   225→        },
   226→        body: JSON.stringify({
   227→          thaiWatsadu: thaiWatsuduInput,
   228→          competitors: validCompetitors.map((entry) => ({
   229→            retailer: entry.retailer,
   230→            url: entry.url,
   231→          })),
   232→        }),
   233→      });
   234→
   235→      if (!response.ok) {
   236→        throw new Error('Failed to process comparison');
   237→      }
   238→
   239→      const result: ComparisonResponse = await response.json();
   240→      setComparisonData(result);
   241→      setStage('results');
   242→
   243→      setToast({
   244→        isVisible: true,
   245→        message: 'Comparison completed successfully!',
   246→        variant: 'success',
   247→      });
   248→    } catch (err) {
   249→      console.error('Error submitting comparison:', err);
   250→      setToast({
   251→        isVisible: true,
   252→        message: 'Failed to process comparison. Please try again.',
   253→        variant: 'error',
   254→      });
   255→      setErrors({ general: 'Failed to process comparison. Please try again.' });
   256→    } finally {
   257→      setIsSubmitting(false);
   258→    }
   259→  }, [thaiWatsuduInput, competitorEntries]);
   260→
   261→  // Start new comparison
   262→  const handleStartNewComparison = useCallback(() => {
   263→    setStage('input');
   264→    setThaiWatsuduInput({ sku: '', url: '' });
   265→    setCompetitorEntries([{ id: `competitor-${Date.now()}`, retailer: '', url: '' }]);
   266→    setComparisonData(null);
   267→    setErrors({});
   268→  }, []);
   269→
   270→  return (
   271→    <MainLayout>
   272→      <div className="space-y-6">
   273→        {/* Breadcrumb */}
   274→        <nav className="text-sm text-gray-500">
   275→          <span>Home</span>
   276→          <span className="mx-2">/</span>
   277→          <span className="font-medium text-gray-900">Manual Comparison</span>
   278→        </nav>
   279→
   280→        {/* Page Header */}
   281→        <div>
   282→          <h1 className="text-3xl font-bold text-gray-900">Manual Comparison</h1>
   283→          <p className="mt-1 text-gray-600">
   284→            Compare products across retailers with Apple-style elegance
   285→          </p>
   286→        </div>
   287→
   288→        {/* Stage Indicator */}
   289→        <StageIndicator currentStage={stage} />
   290→
   291→        {/* STAGE 1: INPUT */}
   292→        {stage === 'input' && (
   293→          <div className="mx-auto max-w-4xl space-y-8">
   294→            {/* Thai Watsadu Input */}
   295→            <div>
   296→              <div className="mb-4 flex items-center gap-3">
   297→                <div className="w-8 h-8 rounded-full bg-red-500 text-white flex items-center justify-center font-bold shadow-md">
   298→                  1
   299→                </div>
   300→                <h2 className="text-2xl font-bold text-gray-900">
   301→                  Thai Watsadu Product
   302→                </h2>
   303→              </div>
   304→              <p className="mb-4 text-sm text-gray-600">
   305→                Enter the details of the product you want to compare from Thai Watsadu
   306→              </p>
   307→              <ThaiWatsuduInputCard
   308→                value={thaiWatsuduInput}
   309→                onChange={setThaiWatsuduInput}
   310→                disabled={false}
   311→                errors={errors.thaiWatsadu}
   312→              />
   313→            </div>
   314→
   315→            {/* Competitor Inputs */}
   316→            <div>
   317→              <div className="mb-4 flex items-center gap-3">
   318→                <div className="w-8 h-8 rounded-full bg-cyan-500 text-white flex items-center justify-center font-bold shadow-md">
   319→                  2
   320→                </div>
   321→                <h2 className="text-2xl font-bold text-gray-900">
   322→                  Competitor Products
   323→                </h2>
   324→              </div>
   325→              <p className="mb-4 text-sm text-gray-600">
   326→                Add up to 5 competitor products to compare against
   327→              </p>
   328→              <div className="space-y-4">
   329→                {competitorEntries.map((entry) => (
   330→                  <CompetitorInputCard
   331→                    key={entry.id}
   332→                    id={entry.id}
   333→                    retailer={entry.retailer}
   334→                    url={entry.url}
   335→                    onRetailerChange={(retailer) =>
   336→                      handleCompetitorRetailerChange(entry.id, retailer)
   337→                    }
   338→                    onUrlChange={(url) => handleCompetitorUrlChange(entry.id, url)}
   339→                    onRemove={() => handleRemoveCompetitor(entry.id)}
   340→                    disabled={false}
   341→                    error={errors.competitors?.[entry.id]}
   342→                    usedRetailers={getUsedRetailers()}
   343→                  />
   344→                ))}
   345→
   346→                {/* Add Competitor Button */}
   347→                {competitorEntries.length < 5 ? (
   348→                  <button
