     1→'use client';
     2→
     3→import React, { useState, useEffect, useCallback } from 'react';
     4→import { useParams, useRouter } from 'next/navigation';
     5→import Link from 'next/link';
     6→import { MainLayout } from '@/components/layout/MainLayout';
     7→import { ProductDetailCard } from '@/components/products/ProductDetailCard';
     8→import { MatchedProductCard } from '@/components/products/MatchedProductCard';
     9→import { ManualComparisonButton } from '@/components/products/ManualComparisonButton';
    10→import {
    11→  ProductComparisonDetailResponse,
    12→  RetailerMatch,
    13→  ValidationStatus,
    14→  LegacyMatchedProduct,
    15→  Retailer,
    16→} from '@/lib/types/price-comparison';
    17→import {
    18→  getAllValidationStatuses,
    19→  setValidationStatus,
    20→  getManualComparison,
    21→  clearValidationStatuses,
    22→  ManualComparisonData,
    23→} from '@/lib/utils/validation-storage';
    24→import { RotateCcw } from 'lucide-react';
    25→
    26→// Transform legacy API response to new multi-product format
    27→function transformMatchedProducts(
    28→  matchedProducts: LegacyMatchedProduct[] | RetailerMatch[],
    29→  validationStatuses: Record<string, ValidationStatus>
    30→): RetailerMatch[] {
    31→  return matchedProducts.map((match) => {
    32→    // Check if it's already in new format (has 'products' array)
    33→    if ('products' in match && Array.isArray(match.products)) {
    34→      return {
    35→        ...match,
    36→        products: match.products.map((product) => ({
    37→          ...product,
    38→          validationStatus: validationStatuses[product.id] || product.validationStatus,
    39→        })),
    40→      };
    41→    }
    42→
    43→    // Transform legacy format (has 'product' object) to new format
    44→    const legacyMatch = match as LegacyMatchedProduct;
    45→    const productId = `${legacyMatch.retailer}_${legacyMatch.product.sku}`;
    46→    return {
    47→      retailer: legacyMatch.retailer,
    48→      confidence: legacyMatch.confidence,
    49→      matchType: legacyMatch.matchType,
    50→      products: [
    51→        {
    52→          id: productId,
    53→          name: legacyMatch.product.name,
    54→          sku: legacyMatch.product.sku,
    55→          imageUrl: legacyMatch.product.imageUrl,
    56→          price: legacyMatch.product.price,
    57→          url: legacyMatch.product.url,
    58→          description: legacyMatch.product.description,
    59→          brand: legacyMatch.product.brand,
    60→          category: legacyMatch.product.category,
    61→          validationStatus: validationStatuses[productId],
    62→        },
    63→      ],
    64→    };
    65→  });
    66→}
    67→
    68→// Filter out products marked as incorrect
    69→function filterIncorrectProducts(matchedProducts: RetailerMatch[]): RetailerMatch[] {
    70→  return matchedProducts
    71→    .map((match) => ({
    72→      ...match,
    73→      products: match.products.filter(
    74→        (product) => product.validationStatus !== 'incorrect'
    75→      ),
    76→    }))
    77→    .filter((match) => match.products.length > 0);
    78→}
    79→
    80→// Check if all products are marked as incorrect
    81→function areAllProductsIncorrect(
    82→  matchedProducts: RetailerMatch[],
    83→  validationStatuses: Record<string, ValidationStatus>
    84→): boolean {
    85→  const allProductIds = matchedProducts.flatMap((match) =>
    86→    match.products.map((product) => product.id)
    87→  );
    88→
    89→  if (allProductIds.length === 0) return true;
    90→
    91→  return allProductIds.every(
    92→    (id) => validationStatuses[id] === 'incorrect'
    93→  );
    94→}
    95→
    96→export default function ProductDetailPage() {
    97→  const params = useParams();
    98→  const router = useRouter();
    99→  const productId = params.id as string;
   100→
   101→  const [data, setData] = useState<ProductComparisonDetailResponse | null>(null);
   102→  const [isLoading, setIsLoading] = useState(true);
   103→  const [error, setError] = useState<string | null>(null);
   104→  const [validationStatuses, setValidationStatuses] = useState<Record<string, ValidationStatus>>({});
   105→  const [showManualForm, setShowManualForm] = useState(false);
   106→  const [manualComparisonData, setManualComparisonData] = useState<ManualComparisonData | null>(null);
   107→
   108→  // Load validation statuses and manual comparison from localStorage on mount
   109→  useEffect(() => {
   110→    if (productId) {
   111→      const statuses = getAllValidationStatuses(productId);
   112→      setValidationStatuses(statuses);
   113→      const manualData = getManualComparison(productId);
   114→      setManualComparisonData(manualData);
   115→    }
   116→  }, [productId]);
   117→
   118→  // Fetch product detail
   119→  useEffect(() => {
   120→    const fetchProductDetail = async () => {
   121→      setIsLoading(true);
   122→      setError(null);
   123→      try {
   124→        const response = await fetch(`/api/products/comparison/${productId}`);
   125→        if (!response.ok) {
   126→          if (response.status === 404) {
   127→            throw new Error('Product not found');
   128→          }
   129→          throw new Error('Failed to fetch product details');
   130→        }
   131→        const result: ProductComparisonDetailResponse = await response.json();
   132→        setData(result);
   133→      } catch (err) {
   134→        setError(err instanceof Error ? err.message : 'An error occurred');
   135→      } finally {
   136→        setIsLoading(false);
   137→      }
   138→    };
   139→
   140→    if (productId) {
   141→      fetchProductDetail();
   142→    }
   143→  }, [productId]);
   144→
   145→  // Handle marking product as correct
   146→  const handleMarkCorrect = useCallback((matchedProductId: string) => {
   147→    setValidationStatus(productId, matchedProductId, 'correct');
   148→    setValidationStatuses((prev) => ({
   149→      ...prev,
   150→      [matchedProductId]: 'correct',
   151→    }));
   152→  }, [productId]);
   153→
   154→  // Handle marking product as incorrect
   155→  const handleMarkIncorrect = useCallback((matchedProductId: string) => {
   156→    setValidationStatus(productId, matchedProductId, 'incorrect');
   157→    setValidationStatuses((prev) => ({
   158→      ...prev,
   159→      [matchedProductId]: 'incorrect',
   160→    }));
   161→  }, [productId]);
   162→
   163→  // Handle reset validations
   164→  const handleResetValidations = useCallback(() => {
   165→    clearValidationStatuses(productId);
   166→    setValidationStatuses({});
   167→    setShowManualForm(false);
   168→  }, [productId]);
   169→
   170→  // Transform and filter matched products
   171→  const transformedMatchedProducts = data
   172→    ? transformMatchedProducts(
   173→        data.matchedProducts as (LegacyMatchedProduct[] | RetailerMatch[]),
   174→        validationStatuses
   175→      )
   176→    : [];
   177→
   178→  const filteredMatchedProducts = filterIncorrectProducts(transformedMatchedProducts);
   179→
   180→  // Check if we should show manual form
   181→  const shouldShowManualForm =
   182→    showManualForm ||
   183→    (transformedMatchedProducts.length > 0 &&
   184→      areAllProductsIncorrect(transformedMatchedProducts, validationStatuses));
   185→
   186→  // Count total and filtered products
   187→  const totalProducts = transformedMatchedProducts.reduce(
   188→    (sum, match) => sum + match.products.length,
   189→    0
   190→  );
   191→  const visibleProducts = filteredMatchedProducts.reduce(
   192→    (sum, match) => sum + match.products.length,
   193→    0
   194→  );
   195→  const hasHiddenProducts = totalProducts > visibleProducts;
   196→
   197→  // Loading state
   198→  if (isLoading) {
   199→    return (
   200→      <MainLayout>
   201→        <div className="space-y-6">
   202→          {/* Breadcrumb skeleton */}
   203→          <div className="h-6 w-48 bg-gray-200 rounded animate-pulse" />
   204→
   205→          <div className="grid grid-cols-1 lg:grid-cols-12 gap-6">
   206→            {/* Left panel skeleton */}
   207→            <div className="lg:col-span-5">
   208→              <div className="bg-white rounded-lg border border-gray-200 p-4 space-y-4">
   209→                <div className="h-8 w-32 bg-gray-200 rounded animate-pulse" />
   210→                <div className="aspect-square bg-gray-200 rounded animate-pulse" />
   211→                <div className="space-y-2">
   212→                  <div className="h-6 bg-gray-200 rounded animate-pulse" />
   213→                  <div className="h-4 w-3/4 bg-gray-200 rounded animate-pulse" />
   214→                </div>
   215→              </div>
   216→            </div>
   217→
   218→            {/* Right panel skeleton */}
   219→            <div className="lg:col-span-7 space-y-4">
   220→              {[1, 2, 3].map((i) => (
   221→                <div
   222→                  key={i}
   223→                  className="bg-white rounded-lg border border-gray-200 p-4 h-40 animate-pulse"
   224→                >
   225→                  <div className="h-4 w-24 bg-gray-200 rounded mb-4" />
   226→                  <div className="flex gap-4">
   227→                    <div className="w-24 h-24 bg-gray-200 rounded" />
   228→                    <div className="flex-1 space-y-2">
   229→                      <div className="h-4 bg-gray-200 rounded" />
   230→                      <div className="h-3 w-1/2 bg-gray-200 rounded" />
   231→                    </div>
   232→                  </div>
   233→                </div>
   234→              ))}
   235→            </div>
   236→          </div>
   237→        </div>
   238→      </MainLayout>
   239→    );
   240→  }
   241→
   242→  // Error state
   243→  if (error) {
   244→    return (
   245→      <MainLayout>
   246→        <div className="space-y-6">
