     2→import mockData from '@/data/mock-products.json';
     3→import { ProductComparison, ProductComparisonListResponse } from '@/lib/types/price-comparison';
     4→
     5→export async function GET(request: NextRequest) {
     6→  try {
     7→    const searchParams = request.nextUrl.searchParams;
     8→
     9→    // Extract query parameters
    10→    const search = searchParams.get('search') || '';
    11→    const category = searchParams.get('category');
    12→    const brand = searchParams.get('brand');
    13→    const status = searchParams.get('status');
    14→    const page = parseInt(searchParams.get('page') || '1', 10);
    15→    const pageSize = parseInt(searchParams.get('pageSize') || '10', 10);
    16→    const sortBy = searchParams.get('sortBy') || 'name';
    17→    const sortOrder = searchParams.get('sortOrder') || 'asc';
    18→
    19→    let products: ProductComparison[] = mockData.products as ProductComparison[];
    20→
    21→    // Apply search filter
    22→    if (search) {
    23→      const searchLower = search.toLowerCase();
    24→      products = products.filter(
    25→        (p) =>
    26→          p.name.toLowerCase().includes(searchLower) ||
    27→          p.sku.toLowerCase().includes(searchLower) ||
    28→          p.brand.toLowerCase().includes(searchLower) ||
    29→          p.category.toLowerCase().includes(searchLower)
    30→      );
    31→    }
    32→
    33→    // Apply category filter
    34→    if (category) {
    35→      products = products.filter((p) => p.category === category || p.categoryTh === category);
    36→    }
    37→
    38→    // Apply brand filter
    39→    if (brand) {
    40→      products = products.filter((p) => p.brand === brand);
    41→    }
    42→
    43→    // Apply status filter
    44→    if (status) {
    45→      products = products.filter((p) => p.status === status);
    46→    }
    47→
    48→    // Apply sorting
    49→    products.sort((a, b) => {
    50→      let aValue: any;
    51→      let bValue: any;
    52→
    53→      switch (sortBy) {
    54→        case 'name':
    55→          aValue = a.name.toLowerCase();
    56→          bValue = b.name.toLowerCase();
    57→          break;
    58→        case 'sku':
    59→          aValue = a.sku;
    60→          bValue = b.sku;
    61→          break;
    62→        case 'category':
    63→          aValue = a.category.toLowerCase();
    64→          bValue = b.category.toLowerCase();
    65→          break;
    66→        case 'brand':
    67→          aValue = a.brand.toLowerCase();
    68→          bValue = b.brand.toLowerCase();
    69→          break;
    70→        case 'price':
    71→          aValue = a.prices.thai_watsadu.price || 0;
    72→          bValue = b.prices.thai_watsadu.price || 0;
    73→          break;
    74→        case 'status':
    75→          aValue = a.status;
    76→          bValue = b.status;
    77→          break;
    78→        default:
    79→          aValue = a.name.toLowerCase();
    80→          bValue = b.name.toLowerCase();
    81→      }
    82→
    83→      if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
    84→      if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
    85→      return 0;
    86→    });
    87→
    88→    // Calculate pagination
    89→    const total = products.length;
    90→    const totalPages = Math.ceil(total / pageSize);
