     1→'use client';
     2→
     3→import { useState, useCallback, useEffect } from 'react';
     4→import { useSearchParams } from 'next/navigation';
     5→import {
     6→  ThaiWatsuduInput,
     7→  CompetitorRetailer,
     8→} from '@/lib/types/manual-comparison';
     9→import {
    10→  ThaiWatsuduInputCard,
    11→  CompetitorInputCard,
    12→} from '@/components/comparison';
    13→import { ReviewConfirmPanel } from '@/components/comparison/ReviewConfirmPanel';
    14→import { AppleStyleComparisonTable } from '@/components/comparison/AppleStyleComparisonTable';
    15→import { StageIndicator } from '@/components/comparison/StageIndicator';
    16→import { CollapsibleStepCard } from '@/components/comparison/CollapsibleStepCard';
    17→import { Toast, ToastVariant } from '@/components/ui/Toast';
    18→import { LoadingOverlay } from '@/components/comparison/LoadingOverlay';
    19→import { MainLayout } from '@/components/layout/MainLayout';
    20→import { Plus, RotateCcw } from 'lucide-react';
    21→
    22→// Stage type for the 3-stage flow
    23→type ComparisonStage = 'input' | 'review' | 'results';
    24→
    25→// Competitor entry structure
    26→interface CompetitorEntry {
    27→  id: string;
    28→  retailer: CompetitorRetailer | '';
    29→  url: string;
    30→}
    31→
    32→// Comparison product structure
    33→interface ComparisonProduct {
    34→  sku: string;
    35→  name: string;
    36→  price: number;
    37→  discountPercentage?: number;
    38→  unitPrice?: number;
    39→  imageUrl?: string;
    40→  productUrl: string;
    41→  brand?: string;
    42→  category?: string;
    43→  stockStatus?: string;
    44→  retailer: string;
    45→  retailerLogo?: string;
    46→}
    47→
    48→// Comparison response structure
    49→interface ComparisonResponse {
    50→  thaiWatsadu: ComparisonProduct;
    51→  competitors: ComparisonProduct[];
    52→}
    53→
    54→// Validation error types
    55→interface ValidationErrors {
    56→  thaiWatsadu?: {
    57→    sku?: string;
    58→    url?: string;
    59→  };
    60→  competitors?: Record<string, string>;
    61→  general?: string;
    62→}
    63→
    64→export default function ManualComparisonPage() {
    65→  const searchParams = useSearchParams();
    66→
    67→  // Stage management
    68→  const [stage, setStage] = useState<ComparisonStage>('input');
    69→
    70→  // Form state
    71→  const [thaiWatsuduInput, setThaiWatsuduInput] = useState<ThaiWatsuduInput>({
    72→    sku: '',
    73→    url: '',
    74→  });
    75→  const [competitorEntries, setCompetitorEntries] = useState<CompetitorEntry[]>([
    76→    { id: `competitor-${Date.now()}`, retailer: '', url: '' },
    77→  ]);
    78→
    79→  // Results state
    80→  const [comparisonData, setComparisonData] = useState<ComparisonResponse | null>(null);
    81→
    82→  // UI state
    83→  const [isSubmitting, setIsSubmitting] = useState(false);
    84→  const [errors, setErrors] = useState<ValidationErrors>({});
    85→  const [toast, setToast] = useState<{
    86→    isVisible: boolean;
    87→    message: string;
    88→    variant: ToastVariant;
    89→  }>({
    90→    isVisible: false,
    91→    message: '',
    92→    variant: 'success',
    93→  });
    94→
    95→  // Handle query parameters for pre-filling
    96→  useEffect(() => {
    97→    const sku = searchParams.get('sku');
    98→    const productId = searchParams.get('productId');
    99→
   100→    if (sku) {
   101→      setThaiWatsuduInput((prev) => ({
   102→        ...prev,
   103→        sku: sku,
   104→      }));
   105→    }
   106→
   107→    // Store productId for potential future use (e.g., linking back or storing results)
   108→    if (productId) {
   109→      // You can use this productId later to save manual comparison results
   110→      console.log('Pre-filled from product:', productId);
   111→    }
   112→  }, [searchParams]);
   113→
   114→  // Add new competitor entry
   115→  const handleAddCompetitor = useCallback(() => {
   116→    const newEntry: CompetitorEntry = {
   117→      id: `competitor-${Date.now()}-${Math.random()}`,
   118→      retailer: '',
   119→      url: '',
   120→    };
   121→    setCompetitorEntries((prev) => [...prev, newEntry]);
   122→  }, []);
   123→
   124→  // Remove competitor entry
   125→  const handleRemoveCompetitor = useCallback((id: string) => {
   126→    setCompetitorEntries((prev) => prev.filter((entry) => entry.id !== id));
   127→  }, []);
   128→
   129→  // Update competitor retailer
   130→  const handleCompetitorRetailerChange = useCallback(
   131→    (id: string, retailer: CompetitorRetailer) => {
   132→      setCompetitorEntries((prev) =>
   133→        prev.map((entry) =>
   134→          entry.id === id ? { ...entry, retailer } : entry
   135→        )
   136→      );
   137→    },
   138→    []
   139→  );
   140→
   141→  // Update competitor URL
   142→  const handleCompetitorUrlChange = useCallback((id: string, url: string) => {
   143→    setCompetitorEntries((prev) =>
   144→      prev.map((entry) => (entry.id === id ? { ...entry, url } : entry))
   145→    );
   146→  }, []);
   147→
   148→  // Get list of used retailers
   149→  const getUsedRetailers = useCallback((): CompetitorRetailer[] => {
   150→    return competitorEntries
   151→      .map((entry) => entry.retailer)
   152→      .filter((retailer): retailer is CompetitorRetailer => retailer !== '');
   153→  }, [competitorEntries]);
   154→
   155→  // Validate input stage
   156→  const validateInputStage = useCallback((): boolean => {
   157→    const newErrors: ValidationErrors = {};
   158→
   159→    // Validate Thai Watsadu
   160→    if (!thaiWatsuduInput.sku.trim()) {
   161→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, sku: 'SKU is required' };
   162→    }
   163→    if (!thaiWatsuduInput.url.trim()) {
   164→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, url: 'URL is required' };
   165→    }
   166→
   167→    // Validate competitors
   168→    const competitorErrors: Record<string, string> = {};
   169→    let hasAtLeastOneCompetitor = false;
   170→
   171→    competitorEntries.forEach((entry) => {
   172→      if (entry.retailer && entry.url.trim()) {
   173→        hasAtLeastOneCompetitor = true;
   174→      }
   175→
   176→      if (entry.retailer && !entry.url.trim()) {
   177→        competitorErrors[entry.id] = 'URL is required';
   178→      }
   179→      if (!entry.retailer && entry.url.trim()) {
   180→        competitorErrors[entry.id] = 'Retailer selection is required';
   181→      }
   182→    });
   183→
   184→    if (!hasAtLeastOneCompetitor) {
   185→      newErrors.general = 'Please add at least one competitor with retailer and URL';
   186→    }
   187→
   188→    if (Object.keys(competitorErrors).length > 0) {
   189→      newErrors.competitors = competitorErrors;
   190→    }
   191→
   192→    setErrors(newErrors);
   193→    return Object.keys(newErrors).length === 0;
   194→  }, [thaiWatsuduInput, competitorEntries]);
   195→
   196→  // Handle Input -> Review transition
   197→  const handleGoToReview = useCallback(() => {
   198→    if (validateInputStage()) {
   199→      setStage('review');
   200→    }
   201→  }, [validateInputStage]);
   202→
   203→  // Handle Review -> Input (Edit)
   204→  const handleEditInputs = useCallback(() => {
   205→    setStage('input');
   206→  }, []);
   207→
   208→  // Handle Review -> Results (Submit)
   209→  const handleConfirmAndCompare = useCallback(async () => {
   210→    setIsSubmitting(true);
   211→    setErrors({});
   212→
   213→    try {
   214→      // Filter valid competitor entries
   215→      const validCompetitors = competitorEntries.filter(
   216→        (entry) => entry.retailer && entry.url.trim()
   217→      );
   218→
   219→      const response = await fetch('/api/comparison/manual', {
   220→        method: 'POST',
   221→        headers: {
   222→          'Content-Type': 'application/json',
   223→        },
   224→        body: JSON.stringify({
   225→          thaiWatsadu: thaiWatsuduInput,
   226→          competitors: validCompetitors.map((entry) => ({
   227→            retailer: entry.retailer,
   228→            url: entry.url,
   229→          })),
   230→        }),
   231→      });
   232→
   233→      if (!response.ok) {
   234→        throw new Error('Failed to process comparison');
   235→      }
   236→
   237→      const result: ComparisonResponse = await response.json();
   238→      setComparisonData(result);
   239→      setStage('results');
   240→
   241→      setToast({
   242→        isVisible: true,
   243→        message: 'Comparison completed successfully!',
   244→        variant: 'success',
   245→      });
   246→    } catch (err) {
   247→      console.error('Error submitting comparison:', err);
   248→      setToast({
   249→        isVisible: true,
   250→        message: 'Failed to process comparison. Please try again.',
   251→        variant: 'error',
   252→      });
   253→      setErrors({ general: 'Failed to process comparison. Please try again.' });
   254→    } finally {
   255→      setIsSubmitting(false);
   256→    }
   257→  }, [thaiWatsuduInput, competitorEntries]);
   258→
   259→  // Start new comparison
   260→  const handleStartNewComparison = useCallback(() => {
   261→    setStage('input');
   262→    setThaiWatsuduInput({ sku: '', url: '' });
   263→    setCompetitorEntries([{ id: `competitor-${Date.now()}`, retailer: '', url: '' }]);
   264→    setComparisonData(null);
   265→    setErrors({});
   266→  }, []);
   267→
   268→  return (
   269→    <MainLayout>
   270→      <div className="space-y-6">
   271→        {/* Breadcrumb */}
   272→        <nav className="text-sm text-gray-500">
   273→          <span>Home</span>
   274→          <span className="mx-2">/</span>
   275→          <span className="font-medium text-gray-900">Manual Comparison</span>
   276→        </nav>
   277→
   278→        {/* Page Header */}
   279→        <div>
   280→          <h1 className="text-3xl font-bold text-gray-900">Manual Comparison</h1>
   281→          <p className="mt-1 text-gray-600">
