     3→import { useState, useCallback } from 'react';
     4→import {
     5→  ThaiWatsuduInput,
     6→  CompetitorRetailer,
     7→} from '@/lib/types/manual-comparison';
     8→import {
     9→  ThaiWatsuduInputCard,
    10→  CompetitorInputCard,
    11→} from '@/components/comparison';
    12→import { ReviewConfirmPanel } from '@/components/comparison/ReviewConfirmPanel';
    13→import { AppleStyleComparisonTable } from '@/components/comparison/AppleStyleComparisonTable';
    14→import { StageIndicator } from '@/components/comparison/StageIndicator';
    15→import { Toast, ToastVariant } from '@/components/ui/Toast';
    16→import { LoadingOverlay } from '@/components/comparison/LoadingOverlay';
    17→import { MainLayout } from '@/components/layout/MainLayout';
    18→import { Plus, RotateCcw } from 'lucide-react';
    19→
    20→// Stage type for the 3-stage flow
    21→type ComparisonStage = 'input' | 'review' | 'results';
    22→
    23→// Competitor entry structure
    24→interface CompetitorEntry {
    25→  id: string;
    26→  retailer: CompetitorRetailer | '';
    27→  url: string;
    28→}
    29→
    30→// Comparison product structure
    31→interface ComparisonProduct {
    32→  sku: string;
    33→  name: string;
    34→  price: number;
    35→  discountPercentage?: number;
    36→  unitPrice?: number;
    37→  imageUrl?: string;
    38→  productUrl: string;
    39→  brand?: string;
    40→  category?: string;
    41→  stockStatus?: string;
    42→  retailer: string;
    43→  retailerLogo?: string;
    44→}
    45→
    46→// Comparison response structure
    47→interface ComparisonResponse {
    48→  thaiWatsadu: ComparisonProduct;
    49→  competitors: ComparisonProduct[];
    50→}
    51→
    52→// Validation error types
    53→interface ValidationErrors {
    54→  thaiWatsadu?: {
    55→    sku?: string;
    56→    url?: string;
    57→  };
    58→  competitors?: Record<string, string>;
    59→  general?: string;
    60→}
    61→
    62→export default function ManualComparisonPage() {
    63→  // Stage management
    64→  const [stage, setStage] = useState<ComparisonStage>('input');
    65→
    66→  // Form state
    67→  const [thaiWatsuduInput, setThaiWatsuduInput] = useState<ThaiWatsuduInput>({
    68→    sku: '',
    69→    url: '',
    70→  });
    71→  const [competitorEntries, setCompetitorEntries] = useState<CompetitorEntry[]>([
    72→    { id: `competitor-${Date.now()}`, retailer: '', url: '' },
    73→  ]);
    74→
    75→  // Results state
    76→  const [comparisonData, setComparisonData] = useState<ComparisonResponse | null>(null);
    77→
    78→  // UI state
    79→  const [isSubmitting, setIsSubmitting] = useState(false);
    80→  const [errors, setErrors] = useState<ValidationErrors>({});
    81→  const [toast, setToast] = useState<{
    82→    isVisible: boolean;
    83→    message: string;
    84→    variant: ToastVariant;
    85→  }>({
    86→    isVisible: false,
    87→    message: '',
    88→    variant: 'success',
    89→  });
    90→
    91→  // Add new competitor entry
    92→  const handleAddCompetitor = useCallback(() => {
    93→    const newEntry: CompetitorEntry = {
