     1→import { PriceStatus, Retailer, ProductComparison } from '../types/price-comparison';
     2→
     3→/**
     4→ * Calculate price status by comparing Thai Watsadu price with competitors
     5→ * @param thaiWatsaduPrice - Thai Watsadu's price
     6→ * @param competitorPrices - Array of competitor prices (excluding null values)
     7→ * @returns PriceStatus - 'cheapest' | 'higher' | 'same' | 'unavailable'
     8→ */
     9→export function calculateStatus(
    10→  thaiWatsaduPrice: number | null,
    11→  competitorPrices: (number | null)[]
    12→): PriceStatus {
    13→  // Filter out null prices
    14→  const validPrices = competitorPrices.filter((p): p is number => p !== null && p > 0);
    15→
    16→  // If Thai Watsadu price is null or no competitor prices available
    17→  if (!thaiWatsaduPrice || thaiWatsaduPrice <= 0 || validPrices.length === 0) {
    18→    return 'unavailable';
    19→  }
    20→
    21→  const minCompetitorPrice = Math.min(...validPrices);
    22→  const maxCompetitorPrice = Math.max(...validPrices);
    23→
    24→  // Define threshold for "same" (within 2%)
    25→  const threshold = 0.02;
    26→
    27→  if (thaiWatsaduPrice <= minCompetitorPrice * (1 + threshold)) {
    28→    return 'cheapest';  // Green badge
    29→  } else if (thaiWatsaduPrice >= maxCompetitorPrice * (1 - threshold)) {
    30→    return 'higher';    // Yellow/Orange badge
    31→  } else {
    32→    return 'same';      // Gray badge
    33→  }
    34→}
    35→
    36→/**
    37→ * Find the lowest price among all retailers
    38→ * @param prices - Object containing prices from all retailers
    39→ * @returns Lowest price or null if no valid prices
    40→ */
    41→export function findLowestPrice(
    42→  prices: ProductComparison['prices']
    43→): number | null {
    44→  const allPrices = Object.values(prices)
    45→    .map(p => p.price)
    46→    .filter((p): p is number => p !== null && p > 0);
    47→
    48→  return allPrices.length > 0 ? Math.min(...allPrices) : null;
    49→}
    50→
