     1→// Retailer enum
     2→export enum Retailer {
     3→  THAI_WATSADU = 'thai_watsadu',
     4→  HOMEPRO = 'homepro',
     5→  GLOBAL_HOUSE = 'global_house',
     6→  DOHOME = 'dohome',
     7→  BOONTHAVORN = 'boonthavorn',
     8→}
     9→
    10→// Retailer display names (Thai + English)
    11→export const RetailerNames: Record<Retailer, { th: string; en: string }> = {
    12→  [Retailer.THAI_WATSADU]: { th: 'ไทวัสดุ', en: 'Thai Watsadu' },
    13→  [Retailer.HOMEPRO]: { th: 'โฮมโปร', en: 'HomePro' },
    14→  [Retailer.GLOBAL_HOUSE]: { th: 'โกลบอลเฮ้าส์', en: 'Global House' },
    15→  [Retailer.DOHOME]: { th: 'ดูโฮม', en: 'DoHome' },
    16→  [Retailer.BOONTHAVORN]: { th: 'บุญถาวร', en: 'Boonthavorn' },
    17→};
    18→
    19→// Price status type
    20→export type PriceStatus = 'cheapest' | 'higher' | 'same' | 'unavailable';
    21→
    22→// Validation status type
    23→export type ValidationStatus = 'pending' | 'correct' | 'incorrect';
    24→
    25→// Individual retailer price info
    26→export interface RetailerPrice {
    27→  retailer: Retailer;
    28→  price: number | null;        // null if unavailable
    29→  originalPrice?: number;      // for discount calculation
    30→  currency: 'THB';
    31→  productUrl: string | null;   // URL to product page
    32→  lastUpdated: string;         // ISO date string
    33→  inStock: boolean;
    34→  promoText?: string;          // e.g., "ลด 20%"
    35→}
    36→
    37→// Product comparison entry
    38→export interface ProductComparison {
    39→  id: string;
    40→  sku: string;
    41→  name: string;
    42→  nameTh?: string;             // Thai name if available
    43→  description?: string;        // Product description
    44→  descriptionTh?: string;      // Thai product description
    45→  category: string;
    46→  categoryTh?: string;         // Thai category name
    47→  brand: string;
    48→  imageUrl: string;
    49→
    50→  // Prices from each retailer
    51→  prices: {
    52→    [Retailer.THAI_WATSADU]: RetailerPrice;
    53→    [Retailer.HOMEPRO]: RetailerPrice;
    54→    [Retailer.GLOBAL_HOUSE]: RetailerPrice;
    55→    [Retailer.DOHOME]: RetailerPrice;
    56→    [Retailer.BOONTHAVORN]: RetailerPrice;
    57→  };
    58→
    59→  // Computed fields
    60→  status: PriceStatus;
    61→  lowestPrice: number | null;
    62→  highestPrice: number | null;
    63→  priceDifferencePercent: number | null;  // % difference between Thai Watsadu and lowest competitor
    64→}
    65→
    66→// Filter state
    67→export interface ProductFilterState {
    68→  search: string;
    69→  category: string | null;
    70→  brand: string | null;
    71→  status: PriceStatus | null;
    72→  sortBy: 'name' | 'sku' | 'category' | 'brand' | 'price' | 'status';
    73→  sortOrder: 'asc' | 'desc';
    74→}
    75→
    76→// Pagination state
