    15→import { CollapsibleStepCard } from '@/components/comparison/CollapsibleStepCard';
    16→import { Toast, ToastVariant } from '@/components/ui/Toast';
    17→import { LoadingOverlay } from '@/components/comparison/LoadingOverlay';
    18→import { MainLayout } from '@/components/layout/MainLayout';
    19→import { Plus, RotateCcw } from 'lucide-react';
    20→
    21→// Stage type for the 3-stage flow
    22→type ComparisonStage = 'input' | 'review' | 'results';
    23→
    24→// Competitor entry structure
    25→interface CompetitorEntry {
    26→  id: string;
    27→  retailer: CompetitorRetailer | '';
    28→  url: string;
    29→}
    30→
    31→// Comparison product structure
    32→interface ComparisonProduct {
    33→  sku: string;
    34→  name: string;
    35→  price: number;
    36→  discountPercentage?: number;
    37→  unitPrice?: number;
    38→  imageUrl?: string;
    39→  productUrl: string;
    40→  brand?: string;
    41→  category?: string;
    42→  stockStatus?: string;
    43→  retailer: string;
    44→  retailerLogo?: string;
    45→}
    46→
    47→// Comparison response structure
    48→interface ComparisonResponse {
    49→  thaiWatsadu: ComparisonProduct;
    50→  competitors: ComparisonProduct[];
    51→}
    52→
    53→// Validation error types
    54→interface ValidationErrors {
    55→  thaiWatsadu?: {
    56→    sku?: string;
