   200→          'Content-Type': 'application/json',
   201→        },
   202→        body: JSON.stringify({
   203→          thaiWatsadu: thaiWatsuduInput,
   204→          competitors: validCompetitors.map((entry) => ({
   205→            retailer: entry.retailer,
   206→            url: entry.url,
   207→          })),
   208→        }),
   209→      });
   210→
   211→      if (!response.ok) {
   212→        throw new Error('Failed to process comparison');
   213→      }
   214→
   215→      const result: ComparisonResponse = await response.json();
   216→      setComparisonData(result);
   217→      setStage('results');
   218→
   219→      setToast({
   220→        isVisible: true,
   221→        message: 'Comparison completed successfully!',
   222→        variant: 'success',
   223→      });
   224→    } catch (err) {
   225→      console.error('Error submitting comparison:', err);
   226→      setToast({
   227→        isVisible: true,
   228→        message: 'Failed to process comparison. Please try again.',
   229→        variant: 'error',
   230→      });
   231→      setErrors({ general: 'Failed to process comparison. Please try again.' });
   232→    } finally {
   233→      setIsSubmitting(false);
   234→    }
   235→  }, [thaiWatsuduInput, competitorEntries]);
   236→
   237→  // Start new comparison
   238→  const handleStartNewComparison = useCallback(() => {
   239→    setStage('input');
   240→    setThaiWatsuduInput({ sku: '', url: '' });
   241→    setCompetitorEntries([{ id: `competitor-${Date.now()}`, retailer: '', url: '' }]);
   242→    setComparisonData(null);
   243→    setErrors({});
   244→  }, []);
   245→
   246→  return (
   247→    <MainLayout>
   248→      <div className="space-y-6">
