   261→          </p>
   262→        </div>
   263→
   264→        {/* Stage Indicator */}
   265→        <StageIndicator currentStage={stage} />
   266→
   267→        {/* STAGE 1: INPUT */}
   268→        {stage === 'input' && (
   269→          <div className="mx-auto max-w-4xl space-y-8">
