     1→'use client';
     2→
     3→import { useState, useCallback } from 'react';
     4→import {
     5→  ThaiWatsuduInput,
     6→  CompetitorRetailer,
     7→} from '@/lib/types/manual-comparison';
     8→import {
     9→  ThaiWatsuduInputCard,
    10→  CompetitorInputCard,
    11→} from '@/components/comparison';
    12→import { ReviewConfirmPanel } from '@/components/comparison/ReviewConfirmPanel';
    13→import { AppleStyleComparisonTable } from '@/components/comparison/AppleStyleComparisonTable';
    14→import { StageIndicator } from '@/components/comparison/StageIndicator';
    15→import { CollapsibleStepCard } from '@/components/comparison/CollapsibleStepCard';
    16→import { Toast, ToastVariant } from '@/components/ui/Toast';
    17→import { LoadingOverlay } from '@/components/comparison/LoadingOverlay';
    18→import { MainLayout } from '@/components/layout/MainLayout';
    19→import { Plus, RotateCcw } from 'lucide-react';
    20→
    21→// Stage type for the 3-stage flow
    22→type ComparisonStage = 'input' | 'review' | 'results';
    23→
    24→// Competitor entry structure
    25→interface CompetitorEntry {
    26→  id: string;
    27→  retailer: CompetitorRetailer | '';
    28→  url: string;
    29→}
    30→
    31→// Comparison product structure
    32→interface ComparisonProduct {
    33→  sku: string;
    34→  name: string;
    35→  price: number;
    36→  discountPercentage?: number;
    37→  unitPrice?: number;
    38→  imageUrl?: string;
    39→  productUrl: string;
    40→  brand?: string;
    41→  category?: string;
    42→  stockStatus?: string;
    43→  retailer: string;
    44→  retailerLogo?: string;
    45→}
    46→
    47→// Comparison response structure
    48→interface ComparisonResponse {
    49→  thaiWatsadu: ComparisonProduct;
    50→  competitors: ComparisonProduct[];
    51→}
    52→
    53→// Validation error types
    54→interface ValidationErrors {
    55→  thaiWatsadu?: {
    56→    sku?: string;
    57→    url?: string;
    58→  };
    59→  competitors?: Record<string, string>;
    60→  general?: string;
    61→}
    62→
    63→export default function ManualComparisonPage() {
    64→  // Stage management
    65→  const [stage, setStage] = useState<ComparisonStage>('input');
    66→
    67→  // Form state
    68→  const [thaiWatsuduInput, setThaiWatsuduInput] = useState<ThaiWatsuduInput>({
    69→    sku: '',
    70→    url: '',
    71→  });
    72→  const [competitorEntries, setCompetitorEntries] = useState<CompetitorEntry[]>([
    73→    { id: `competitor-${Date.now()}`, retailer: '', url: '' },
    74→  ]);
    75→
    76→  // Results state
    77→  const [comparisonData, setComparisonData] = useState<ComparisonResponse | null>(null);
    78→
    79→  // UI state
    80→  const [isSubmitting, setIsSubmitting] = useState(false);
    81→  const [errors, setErrors] = useState<ValidationErrors>({});
    82→  const [toast, setToast] = useState<{
    83→    isVisible: boolean;
    84→    message: string;
    85→    variant: ToastVariant;
    86→  }>({
    87→    isVisible: false,
    88→    message: '',
    89→    variant: 'success',
    90→  });
    91→
    92→  // Add new competitor entry
    93→  const handleAddCompetitor = useCallback(() => {
    94→    const newEntry: CompetitorEntry = {
    95→      id: `competitor-${Date.now()}-${Math.random()}`,
    96→      retailer: '',
    97→      url: '',
    98→    };
    99→    setCompetitorEntries((prev) => [...prev, newEntry]);
   100→  }, []);
   101→
   102→  // Remove competitor entry
   103→  const handleRemoveCompetitor = useCallback((id: string) => {
   104→    setCompetitorEntries((prev) => prev.filter((entry) => entry.id !== id));
   105→  }, []);
   106→
   107→  // Update competitor retailer
   108→  const handleCompetitorRetailerChange = useCallback(
   109→    (id: string, retailer: CompetitorRetailer) => {
   110→      setCompetitorEntries((prev) =>
   111→        prev.map((entry) =>
   112→          entry.id === id ? { ...entry, retailer } : entry
   113→        )
   114→      );
   115→    },
   116→    []
   117→  );
   118→
   119→  // Update competitor URL
   120→  const handleCompetitorUrlChange = useCallback((id: string, url: string) => {
   121→    setCompetitorEntries((prev) =>
   122→      prev.map((entry) => (entry.id === id ? { ...entry, url } : entry))
   123→    );
   124→  }, []);
   125→
   126→  // Get list of used retailers
   127→  const getUsedRetailers = useCallback((): CompetitorRetailer[] => {
   128→    return competitorEntries
   129→      .map((entry) => entry.retailer)
   130→      .filter((retailer): retailer is CompetitorRetailer => retailer !== '');
   131→  }, [competitorEntries]);
   132→
   133→  // Validate input stage
   134→  const validateInputStage = useCallback((): boolean => {
   135→    const newErrors: ValidationErrors = {};
   136→
   137→    // Validate Thai Watsadu
   138→    if (!thaiWatsuduInput.sku.trim()) {
   139→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, sku: 'SKU is required' };
   140→    }
   141→    if (!thaiWatsuduInput.url.trim()) {
   142→      newErrors.thaiWatsadu = { ...newErrors.thaiWatsadu, url: 'URL is required' };
   143→    }
   144→
   145→    // Validate competitors
   146→    const competitorErrors: Record<string, string> = {};
   147→    let hasAtLeastOneCompetitor = false;
   148→
   149→    competitorEntries.forEach((entry) => {
   150→      if (entry.retailer && entry.url.trim()) {
   151→        hasAtLeastOneCompetitor = true;
   152→      }
   153→
   154→      if (entry.retailer && !entry.url.trim()) {
   155→        competitorErrors[entry.id] = 'URL is required';
   156→      }
   157→      if (!entry.retailer && entry.url.trim()) {
   158→        competitorErrors[entry.id] = 'Retailer selection is required';
   159→      }
   160→    });
   161→
   162→    if (!hasAtLeastOneCompetitor) {
   163→      newErrors.general = 'Please add at least one competitor with retailer and URL';
   164→    }
   165→
   166→    if (Object.keys(competitorErrors).length > 0) {
   167→      newErrors.competitors = competitorErrors;
   168→    }
   169→
   170→    setErrors(newErrors);
   171→    return Object.keys(newErrors).length === 0;
   172→  }, [thaiWatsuduInput, competitorEntries]);
   173→
   174→  // Handle Input -> Review transition
   175→  const handleGoToReview = useCallback(() => {
   176→    if (validateInputStage()) {
   177→      setStage('review');
   178→    }
   179→  }, [validateInputStage]);
   180→
   181→  // Handle Review -> Input (Edit)
   182→  const handleEditInputs = useCallback(() => {
   183→    setStage('input');
   184→  }, []);
   185→
   186→  // Handle Review -> Results (Submit)
   187→  const handleConfirmAndCompare = useCallback(async () => {
   188→    setIsSubmitting(true);
   189→    setErrors({});
   190→
   191→    try {
   192→      // Filter valid competitor entries
   193→      const validCompetitors = competitorEntries.filter(
   194→        (entry) => entry.retailer && entry.url.trim()
   195→      );
   196→
   197→      const response = await fetch('/api/comparison/manual', {
   198→        method: 'POST',
   199→        headers: {
   200→          'Content-Type': 'application/json',
   201→        },
   202→        body: JSON.stringify({
   203→          thaiWatsadu: thaiWatsuduInput,
   204→          competitors: validCompetitors.map((entry) => ({
   205→            retailer: entry.retailer,
   206→            url: entry.url,
   207→          })),
   208→        }),
   209→      });
   210→
   211→      if (!response.ok) {
   212→        throw new Error('Failed to process comparison');
   213→      }
   214→
   215→      const result: ComparisonResponse = await response.json();
   216→      setComparisonData(result);
   217→      setStage('results');
   218→
   219→      setToast({
   220→        isVisible: true,
   221→        message: 'Comparison completed successfully!',
   222→        variant: 'success',
   223→      });
   224→    } catch (err) {
   225→      console.error('Error submitting comparison:', err);
   226→      setToast({
   227→        isVisible: true,
   228→        message: 'Failed to process comparison. Please try again.',
   229→        variant: 'error',
   230→      });
   231→      setErrors({ general: 'Failed to process comparison. Please try again.' });
   232→    } finally {
   233→      setIsSubmitting(false);
   234→    }
   235→  }, [thaiWatsuduInput, competitorEntries]);
   236→
   237→  // Start new comparison
   238→  const handleStartNewComparison = useCallback(() => {
   239→    setStage('input');
   240→    setThaiWatsuduInput({ sku: '', url: '' });
   241→    setCompetitorEntries([{ id: `competitor-${Date.now()}`, retailer: '', url: '' }]);
   242→    setComparisonData(null);
   243→    setErrors({});
   244→  }, []);
   245→
   246→  return (
   247→    <MainLayout>
   248→      <div className="space-y-6">
   249→        {/* Breadcrumb */}
   250→        <nav className="text-sm text-gray-500">
   251→          <span>Home</span>
   252→          <span className="mx-2">/</span>
   253→          <span className="font-medium text-gray-900">Manual Comparison</span>
   254→        </nav>
   255→
   256→        {/* Page Header */}
   257→        <div>
   258→          <h1 className="text-3xl font-bold text-gray-900">Manual Comparison</h1>
   259→          <p className="mt-1 text-gray-600">
   260→            Compare products across retailers with Apple-style elegance
   261→          </p>
   262→        </div>
   263→
   264→        {/* Stage Indicator */}
   265→        <StageIndicator currentStage={stage} />
   266→
   267→        {/* STAGE 1: INPUT */}
   268→        {stage === 'input' && (
   269→          <div className="mx-auto max-w-4xl space-y-8">
   270→            {/* Thai Watsadu Input */}
   271→            <div>
   272→              <div className="mb-4 flex items-center gap-3">
   273→                <div className="w-8 h-8 rounded-full bg-red-500 text-white flex items-center justify-center font-bold shadow-md">
   274→                  1
   275→                </div>
   276→                <h2 className="text-2xl font-bold text-gray-900">
   277→                  Thai Watsadu Product
   278→                </h2>
   279→              </div>
   280→              <p className="mb-4 text-sm text-gray-600">
   281→                Enter the details of the product you want to compare from Thai Watsadu
   282→              </p>
   283→              <ThaiWatsuduInputCard
   284→                value={thaiWatsuduInput}
   285→                onChange={setThaiWatsuduInput}
   286→                disabled={false}
   287→                errors={errors.thaiWatsadu}
   288→              />
   289→            </div>
   290→
   291→            {/* Competitor Inputs */}
   292→            <div>
   293→              <div className="mb-4 flex items-center gap-3">
   294→                <div className="w-8 h-8 rounded-full bg-cyan-500 text-white flex items-center justify-center font-bold shadow-md">
   295→                  2
   296→                </div>
   297→                <h2 className="text-2xl font-bold text-gray-900">
   298→                  Competitor Products
   299→                </h2>
   300→              </div>
   301→              <p className="mb-4 text-sm text-gray-600">
   302→                Add up to 5 competitor products to compare against
   303→              </p>
   304→              <div className="space-y-4">
   305→                {competitorEntries.map((entry) => (
   306→                  <CompetitorInputCard
   307→                    key={entry.id}
   308→                    id={entry.id}
   309→                    retailer={entry.retailer}
   310→                    url={entry.url}
   311→                    onRetailerChange={(retailer) =>
   312→                      handleCompetitorRetailerChange(entry.id, retailer)
   313→                    }
   314→                    onUrlChange={(url) => handleCompetitorUrlChange(entry.id, url)}
   315→                    onRemove={() => handleRemoveCompetitor(entry.id)}
   316→                    disabled={false}
   317→                    error={errors.competitors?.[entry.id]}
   318→                    usedRetailers={getUsedRetailers()}
   319→                  />
   320→                ))}
   321→
   322→                {/* Add Competitor Button */}
   323→                {competitorEntries.length < 5 ? (
   324→                  <button
   325→                    type="button"
   326→                    onClick={handleAddCompetitor}
   327→                    className="group flex w-full items-center justify-center gap-3 rounded-xl border-2 border-dashed border-cyan-400 bg-gradient-to-r from-cyan-50 to-cyan-100 px-6 py-5 text-cyan-700 transition-all duration-200 hover:border-cyan-500 hover:from-cyan-100 hover:to-cyan-200 hover:shadow-lg hover:scale-[1.02] active:scale-[0.98]"
   328→                  >
   329→                    <div className="rounded-full bg-cyan-500 p-1.5 group-hover:bg-cyan-600 transition-colors shadow-md">
   330→                      <Plus className="h-5 w-5 text-white" />
   331→                    </div>
   332→                    <span className="font-bold text-base">
   333→                      Add Competitor ({competitorEntries.length}/5)
   334→                    </span>
   335→                  </button>
