    96→      setErrors({ retailers: 'Please select at least one retailer' });
    97→      return false;
    98→    }
    99→    setErrors({});
   100→    return true;
   101→  }, [selectedRetailers]);
   102→
   103→  // Validate Stage 3: Competitor URLs
