   105→    const urlErrors: Record<string, string> = {};
   106→
   107→    competitorEntries.forEach((entry) => {
   108→      const validUrls = entry.urls.filter((url) => url.trim() !== '');
   109→      if (validUrls.length === 0) {
   110→        urlErrors[entry.id] = 'At least one URL is required';
   111→      }
   112→    });
   113→
   114→    if (Object.keys(urlErrors).length > 0) {
   115→      setErrors({ competitorUrls: urlErrors });
   116→      return false;
   117→    }
   118→
   119→    setErrors({});
   120→    return true;
   121→  }, [competitorEntries]);
   122→
   123→  // Handle Stage 1 -> Stage 2 transition
   124→  const handleGoToRetailerSelection = useCallback(() => {
   125→    if (validateThaiWatsuduInput()) {
   126→      setStage('selecting');
   127→    }
   128→  }, [validateThaiWatsuduInput]);
   129→
   130→  // Handle Stage 2 -> Stage 3 transition
