   291→          <span className="text-gray-900 font-medium">Manual Comparison</span>
   292→        </nav>
   293→
   294→        {/* Page Header */}
   295→        <div>
   296→          <h1 className="text-3xl font-bold text-gray-900">Manual Comparison</h1>
   297→          <p className="text-gray-600 mt-1">Compare products across retailers step by step</p>
   298→        </div>
   299→
   300→        {/* Stage Indicator */}
   301→        <StageIndicator currentStage={stage} />
   302→
   303→        {/* Single Column Layout with Step Cards */}
   304→        <div className="max-w-4xl mx-auto space-y-6">
   305→          {/* Step 1: Thai Watsadu Input */}
   306→          <StepCard
   307→            title="Thai Watsadu Product"
   308→            stepNumber={1}
   309→            isActive={stage === 'input'}
   310→            isCompleted={isStepCompleted('input')}
   311→            allowExpand={true}
   312→          >
   313→            <div className="space-y-4">
   314→              <ThaiWatsuduInputCard
   315→                value={thaiWatsuduInput}
   316→                onChange={setThaiWatsuduInput}
   317→                disabled={stage !== 'input'}
   318→                errors={errors.thaiWatsadu}
   319→              />
   320→
   321→              {stage === 'input' && (
   322→                <div className="flex justify-end">
   323→                  <button
   324→                    type="button"
   325→                    onClick={handleGoToRetailerSelection}
   326→                    className="px-8 py-3 bg-cyan-500 text-white rounded-lg hover:bg-cyan-600 shadow-md font-semibold transition-all disabled:opacity-50 disabled:cursor-not-allowed"
   327→                  >
