   329→                  </button>
   330→                </div>
   331→              )}
   332→            </div>
   333→          </StepCard>
   334→
   335→          {/* Step 2: Retailer Selection */}
   336→          {(stage === 'selecting' || stage === 'competitor_urls' || stage === 'validation') && (
   337→            <StepCard
   338→              title="Select Retailers"
   339→              stepNumber={2}
   340→              isActive={stage === 'selecting'}
   341→              isCompleted={isStepCompleted('selecting')}
   342→              allowExpand={true}
   343→            >
   344→              <div className="space-y-4">
   345→                <RetailerSelector
   346→                  selectedRetailers={selectedRetailers}
   347→                  onSelect={handleRetailerSelect}
   348→                  disabled={stage !== 'selecting'}
   349→                />
   350→
   351→                {errors.retailers && stage === 'selecting' && (
   352→                  <p className="text-sm text-red-600 font-medium">{errors.retailers}</p>
   353→                )}
   354→
   355→                {stage === 'selecting' && (
   356→                  <div className="flex justify-between">
   357→                    <button
   358→                      type="button"
   359→                      onClick={handleBack}
   360→                      className="flex items-center gap-2 px-6 py-2.5 border-2 border-gray-300 text-gray-700 hover:bg-gray-50 rounded-lg font-medium transition-colors"
   361→                    >
   362→                      <ChevronLeft className="w-4 h-4" />
   363→                      <span>Back</span>
   364→                    </button>
   365→                    <button
   366→                      type="button"
   367→                      onClick={handleGoToCompetitorUrls}
   368→                      className="px-8 py-3 bg-cyan-500 text-white rounded-lg hover:bg-cyan-600 shadow-md font-semibold transition-all disabled:opacity-50 disabled:cursor-not-allowed"
   369→                    >
   370→                      Next
   371→                    </button>
   372→                  </div>
   373→                )}
   374→              </div>
   375→            </StepCard>
   376→          )}
   377→
   378→          {/* Step 3: Competitor URLs */}
   379→          {(stage === 'competitor_urls' || stage === 'validation') && (
   380→            <StepCard
   381→              title="Enter Competitor URLs"
   382→              stepNumber={3}
   383→              isActive={stage === 'competitor_urls'}
   384→              isCompleted={isStepCompleted('competitor_urls')}
   385→              allowExpand={true}
   386→            >
   387→              <div className="space-y-4">
   388→                <div className="space-y-4">
   389→                  {competitorEntries.map((entry) => (
   390→                    <CompetitorInputCard
   391→                      key={entry.id}
   392→                      entry={entry}
   393→                      onChange={handleCompetitorEntryChange}
   394→                      onRemove={() => handleCompetitorEntryRemove(entry.id)}
   395→                      disabled={stage !== 'competitor_urls' || isSubmitting}
   396→                      error={errors.competitorUrls?.[entry.id]}
   397→                    />
   398→                  ))}
   399→                </div>
   400→
   401→                {stage === 'competitor_urls' && competitorEntries.length > 0 && (
   402→                  <div className="pt-4 border-t-2 border-gray-200">
   403→                    <ComparisonSummaryCard
   404→                      thaiWatsadu={thaiWatsuduInput}
   405→                      competitorEntries={competitorEntries}
   406→                      onEdit={() => setStage('input')}
   407→                    />
   408→                  </div>
   409→                )}
   410→
   411→                {stage === 'competitor_urls' && (
   412→                  <div className="flex justify-between pt-4">
   413→                    <button
   414→                      type="button"
   415→                      onClick={handleBack}
   416→                      disabled={isSubmitting}
   417→                      className="flex items-center gap-2 px-6 py-2.5 border-2 border-gray-300 text-gray-700 hover:bg-gray-50 rounded-lg font-medium transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
   418→                    >
   419→                      <ChevronLeft className="w-4 h-4" />
   420→                      <span>Back</span>
   421→                    </button>
   422→                    <button
   423→                      type="button"
   424→                      onClick={handleSubmitComparison}
   425→                      disabled={isSubmitting || competitorEntries.length === 0}
   426→                      className="flex items-center gap-2 px-8 py-3 bg-cyan-500 text-white rounded-lg hover:bg-cyan-600 shadow-md font-semibold transition-all disabled:opacity-50 disabled:cursor-not-allowed"
   427→                    >
   428→                      {isSubmitting && <Loader2 className="w-5 h-5 animate-spin" />}
   429→                      <span>Compare</span>
   430→                    </button>
   431→                  </div>
