   436→
   437→          {/* Step 4: Validation */}
   438→          {stage === 'validation' && (
   439→            <StepCard
   440→              title="Validate Results"
   441→              stepNumber={4}
   442→              isActive={stage === 'validation'}
   443→              isCompleted={false}
   444→              allowExpand={false}
   445→            >
   446→              <UserValidationPanel
   447→                results={results}
   448→                onRetry={handleRetry}
   449→                onConfirm={handleConfirm}
   450→                isProcessing={isSubmitting}
   451→              />
   452→            </StepCard>
   453→          )}
   454→        </div>
   455→
   456→        {/* Comparison ID (shown in validation stage) */}
   457→        {stage === 'validation' && comparisonId && (
