'use client';

import { CompetitorUrlEntry } from '@/lib/types/manual-comparison';
import { COMPETITORS } from '@/lib/constants/competitors';
import { Plus, X } from 'lucide-react';
import { Tooltip } from '@/components/ui/Tooltip';
import Image from 'next/image';

interface CompetitorInputCardProps {
  entry: CompetitorUrlEntry;
  onChange: (entry: CompetitorUrlEntry) => void;
  onRemove: () => void;
  disabled?: boolean;
  error?: string;
}

export function CompetitorInputCard({
  entry,
  onChange,
  onRemove,
  disabled = false,
  error,
}: CompetitorInputCardProps) {
  const competitor = COMPETITORS[entry.retailer];
  const validUrlCount = entry.urls.filter(url => url.trim() !== '').length;

  const handleAddUrl = () => {
    onChange({
      ...entry,
      urls: [...entry.urls, ''],
    });
  };

  const handleUrlChange = (index: number, url: string) => {
    const newUrls = [...entry.urls];
    newUrls[index] = url;
    onChange({ ...entry, urls: newUrls });
  };

  const handleRemoveUrl = (index: number) => {
    if (entry.urls.length <= 1) return;
    const newUrls = entry.urls.filter((_, i) => i !== index);
    onChange({ ...entry, urls: newUrls });
  };

  return (
    <div
      className="bg-white rounded-xl overflow-hidden shadow-md hover:shadow-lg transition-all"
      style={{ borderLeft: `6px solid ${competitor.color}` }}
    >
      {/* Header with competitor branding */}
      <div className="px-4 py-3 bg-gray-50 flex items-center justify-between border-b-2 border-gray-200">
        <div className="flex items-center gap-2">
          {competitor.logo && (
            <Image
              src={competitor.logo}
              alt={competitor.name}
              width={24}
              height={24}
              className="object-contain"
            />
          )}
          <span className="text-sm font-bold text-gray-900">{competitor.name}</span>
          {validUrlCount > 0 && (
            <span className="text-xs bg-cyan-100 text-cyan-700 px-2 py-0.5 rounded-full font-medium">
              {validUrlCount} {validUrlCount === 1 ? 'URL' : 'URLs'}
            </span>
          )}
        </div>
        <button
          type="button"
          onClick={onRemove}
          disabled={disabled}
          className="p-1 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded disabled:cursor-not-allowed transition-colors"
          aria-label="Remove competitor"
        >
          <X className="w-5 h-5" />
        </button>
      </div>

      {/* URL Inputs */}
      <div className="p-4 space-y-3">
        <div className="flex items-center gap-2">
          <label className="block text-sm font-semibold text-gray-900">Product URLs</label>
          <Tooltip content={`Product URL from ${competitor.name} website`} placement="right">
            <span className="text-gray-400" />
          </Tooltip>
        </div>

        {entry.urls.map((url, index) => (
          <div key={index} className="flex items-center gap-2">
            <div
              className="flex-1 bg-gray-50 rounded-lg px-4 py-3 min-h-[52px] cursor-text border-2 border-gray-200 hover:border-gray-300 focus-within:border-cyan-500 focus-within:ring-2 focus-within:ring-cyan-100 transition-all"
              onClick={() => {
                const input = document.getElementById(`${entry.id}-url-${index}`);
                input?.focus();
              }}
            >
              <input
                id={`${entry.id}-url-${index}`}
                type="url"
                value={url}
                onChange={(e) => handleUrlChange(index, e.target.value)}
                placeholder={index === 0 ? `Enter ${competitor.name} product URL` : `Additional URL ${index + 1}`}
                disabled={disabled}
                className="w-full bg-transparent border-none outline-none text-gray-900 placeholder-gray-400 font-medium disabled:cursor-not-allowed disabled:opacity-50"
              />
            </div>
            {entry.urls.length > 1 && (
              <button
                type="button"
                onClick={() => handleRemoveUrl(index)}
                disabled={disabled}
                className="p-2 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded disabled:cursor-not-allowed transition-colors"
                aria-label="Remove URL"
              >
                <X className="w-5 h-5" />
              </button>
            )}
          </div>
        ))}

        {/* Add URL Button */}
        <button
          type="button"
          onClick={handleAddUrl}
          disabled={disabled}
          className="flex items-center justify-center gap-2 w-full px-4 py-3 text-sm text-cyan-600 hover:text-cyan-700 hover:bg-cyan-50 rounded-lg border-2 border-dashed border-gray-300 hover:border-cyan-300 disabled:opacity-50 disabled:cursor-not-allowed transition-all font-semibold"
        >
          <Plus className="w-5 h-5" />
          <span>Add another URL</span>
        </button>

        {error && (
          <p className="text-xs text-red-600 font-medium">{error}</p>
        )}
      </div>
    </div>
  );
}

