     4→import { useParams, useRouter } from 'next/navigation';
     5→import Link from 'next/link';
     6→import { MainLayout } from '@/components/layout/MainLayout';
     7→import { ProductDetailCard } from '@/components/products/ProductDetailCard';
     8→import { MatchedProductCard } from '@/components/products/MatchedProductCard';
     9→import { ManualComparisonForm } from '@/components/comparison/ManualComparisonForm';
    10→import {
    11→  ProductComparisonDetailResponse,
    12→  RetailerMatch,
    13→  MatchedProduct,
    14→  ValidationStatus,
    15→  LegacyMatchedProduct,
    16→  Retailer,
    17→} from '@/lib/types/price-comparison';
    18→import {
    19→  getAllValidationStatuses,
    20→  setValidationStatus,
    21→  getManualComparison,
    22→  setManualComparison,
    23→  clearValidationStatuses,
    24→  ManualComparisonData,
    25→} from '@/lib/utils/validation-storage';
    26→import { SourceProduct, CompetitorRetailer } from '@/lib/types/manual-comparison';
    27→import { RotateCcw } from 'lucide-react';
    28→
    29→// Transform legacy API response to new multi-product format
    30→function transformMatchedProducts(
    31→  matchedProducts: LegacyMatchedProduct[] | RetailerMatch[],
    32→  validationStatuses: Record<string, ValidationStatus>
    33→): RetailerMatch[] {
    34→  return matchedProducts.map((match) => {
    35→    // Check if it's already in new format (has 'products' array)
    36→    if ('products' in match && Array.isArray(match.products)) {
    37→      return {
    38→        ...match,
    39→        products: match.products.map((product) => ({
    40→          ...product,
    41→          validationStatus: validationStatuses[product.id] || product.validationStatus,
    42→        })),
    43→      };
    44→    }
    45→
    46→    // Transform legacy format (has 'product' object) to new format
    47→    const legacyMatch = match as LegacyMatchedProduct;
    48→    const productId = `${legacyMatch.retailer}_${legacyMatch.product.sku}`;
    49→    return {
    50→      retailer: legacyMatch.retailer,
    51→      confidence: legacyMatch.confidence,
    52→      matchType: legacyMatch.matchType,
    53→      products: [
    54→        {
    55→          id: productId,
    56→          name: legacyMatch.product.name,
    57→          sku: legacyMatch.product.sku,
    58→          imageUrl: legacyMatch.product.imageUrl,
    59→          price: legacyMatch.product.price,
    60→          url: legacyMatch.product.url,
    61→          description: legacyMatch.product.description,
    62→          brand: legacyMatch.product.brand,
    63→          category: legacyMatch.product.category,
    64→          validationStatus: validationStatuses[productId],
    65→        },
    66→      ],
    67→    };
    68→  });
    69→}
    70→
    71→// Filter out products marked as incorrect
    72→function filterIncorrectProducts(matchedProducts: RetailerMatch[]): RetailerMatch[] {
    73→  return matchedProducts
    74→    .map((match) => ({
    75→      ...match,
    76→      products: match.products.filter(
    77→        (product) => product.validationStatus !== 'incorrect'
    78→      ),
    79→    }))
    80→    .filter((match) => match.products.length > 0);
    81→}
    82→
    83→// Check if all products are marked as incorrect
    84→function areAllProductsIncorrect(
    85→  matchedProducts: RetailerMatch[],
    86→  validationStatuses: Record<string, ValidationStatus>
    87→): boolean {
    88→  const allProductIds = matchedProducts.flatMap((match) =>
    89→    match.products.map((product) => product.id)
    90→  );
    91→
    92→  if (allProductIds.length === 0) return true;
    93→
    94→  return allProductIds.every(
    95→    (id) => validationStatuses[id] === 'incorrect'
    96→  );
    97→}
    98→
    99→export default function ProductDetailPage() {
   100→  const params = useParams();
   101→  const router = useRouter();
   102→  const productId = params.id as string;
   103→
   104→  const [data, setData] = useState<ProductComparisonDetailResponse | null>(null);
   105→  const [isLoading, setIsLoading] = useState(true);
   106→  const [error, setError] = useState<string | null>(null);
   107→  const [validationStatuses, setValidationStatuses] = useState<Record<string, ValidationStatus>>({});
   108→  const [showManualForm, setShowManualForm] = useState(false);
   109→  const [manualComparisonData, setManualComparisonData] = useState<ManualComparisonData | null>(null);
   110→
   111→  // Load validation statuses and manual comparison from localStorage on mount
   112→  useEffect(() => {
   113→    if (productId) {
   114→      const statuses = getAllValidationStatuses(productId);
   115→      setValidationStatuses(statuses);
   116→      const manualData = getManualComparison(productId);
   117→      setManualComparisonData(manualData);
   118→    }
   119→  }, [productId]);
   120→
   121→  // Fetch product detail
   122→  useEffect(() => {
   123→    const fetchProductDetail = async () => {
   124→      setIsLoading(true);
   125→      setError(null);
   126→      try {
   127→        const response = await fetch(`/api/products/comparison/${productId}`);
   128→        if (!response.ok) {
   129→          if (response.status === 404) {
   130→            throw new Error('Product not found');
   131→          }
   132→          throw new Error('Failed to fetch product details');
   133→        }
   134→        const result: ProductComparisonDetailResponse = await response.json();
   135→        setData(result);
   136→      } catch (err) {
   137→        setError(err instanceof Error ? err.message : 'An error occurred');
   138→      } finally {
   139→        setIsLoading(false);
   140→      }
   141→    };
   142→
   143→    if (productId) {
   144→      fetchProductDetail();
   145→    }
   146→  }, [productId]);
   147→
   148→  // Handle marking product as correct
   149→  const handleMarkCorrect = useCallback((matchedProductId: string) => {
   150→    setValidationStatus(productId, matchedProductId, 'correct');
   151→    setValidationStatuses((prev) => ({
   152→      ...prev,
   153→      [matchedProductId]: 'correct',
   154→    }));
   155→  }, [productId]);
   156→
   157→  // Handle marking product as incorrect
   158→  const handleMarkIncorrect = useCallback((matchedProductId: string) => {
   159→    setValidationStatus(productId, matchedProductId, 'incorrect');
   160→    setValidationStatuses((prev) => ({
   161→      ...prev,
   162→      [matchedProductId]: 'incorrect',
   163→    }));
   164→  }, [productId]);
   165→
   166→  // Handle reset validations
   167→  const handleResetValidations = useCallback(() => {
   168→    clearValidationStatuses(productId);
   169→    setValidationStatuses({});
   170→    setShowManualForm(false);
   171→  }, [productId]);
   172→
   173→  // Handle manual comparison form submission
   174→  const handleManualComparisonSubmit = useCallback((submittedData: {
   175→    sourceProduct: SourceProduct;
   176→    competitors: CompetitorRetailer[];
   177→    competitorUrls: Partial<Record<CompetitorRetailer, string>>;
   178→  }) => {
   179→    // For now, we'll just store a placeholder - in a real app, this would trigger API calls
   180→    const manualData: ManualComparisonData = {
