    84→  status: MatchStatus;
    85→  confidence: number; // 0-100
    86→  matchedProduct?: {
    87→    sku: string;
    88→    name: string;
    89→    price: number;
    90→    imageUrl: string;
    91→    productUrl: string;
    92→  };
